        "Search": function() {
            var _this = this;

            $(document).on("click keydown", "#gb-search-toggle", function(e){
                if(_this.AllyClick(e)) {
                	e.preventDefault();

                    // SEARCH IS CURRENTLY CLOSED
                    if($("#gb-search").attr("aria-hidden") == "true") {
                    	$("#gb-search-toggle").attr("aria-expanded", "true");
                    	$("#gb-search").attr("aria-hidden", "false");
                        $("#gb-search-outer").addClass("open");

                        setTimeout(function() {
                        	$("#gb-search-input").focus();
                        }, 500);
                    }

                    // SEARCH IS CURRENTLY OPEN
                    else {
                    	$("#gb-search-toggle").attr("aria-expanded", "false");
                    	$("#gb-search").attr("aria-hidden", "true");
                        $("#gb-search-outer").removeClass("open");
                    }
                }
            });

            $("#gb-search-form").submit(function(e){
                e.preventDefault();

                if($.trim($("#gb-search-input").val()) != "" && $.trim($("#gb-search-input").val()) != "Search...") {
                	window.location.href = "/site/Default.aspx?PageType=6&SiteID=4&SearchString=" + $("#gb-search-input").val();
                }
            });
        },
        "Slideout": function() {
            var _this = this;

            $(document).on("click keydown", "#slideout-toggle", function(e){
                if(_this.AllyClick(e)) {
                	e.preventDefault();

                    // SLIDEOUT IS OPEN
                    if($("#hp-slideout-outer").attr("aria-expanded") == "true") {
                    	$("#hp-slideout-outer").attr("aria-expanded", "false");
                    	$("#hp-slideout").attr("aria-hidden", "true");
                        $("#hp-slideout a").attr("tabindex","-1");
                        $(this).attr("aria-label","Open Upcoming events slideout");
                    }

                    // SLIDEOUT IS CLOSED
                    else {
                    	$("#hp-slideout-outer").attr("aria-expanded", "true");
                    	$("#hp-slideout").attr("aria-hidden", "false");
                        $("#hp-slideout a").attr("tabindex","0");
                        $(this).attr("aria-label","Close Upcoming events slideout");
                    }
                }
            });

            $("#gb-search-form").submit(function(e){
                e.preventDefault();

                if($.trim($("#gb-search-input").val()) != "" && $.trim($("#gb-search-input").val()) != "Search...") {
                	window.location.href = "/site/Default.aspx?PageType=6&SiteID=4&SearchString=" + $("#gb-search-input").val();
                }
            });
        },


// PLUGIN METHODS
        "Slideshow": function() {
        	if("false" == "false") {
                if($("#sw-content-container10 .ui-widget.app.multimedia-gallery").length) {
                    var mmg = eval("multimediaGallery" + $("#sw-content-container10 .ui-widget.app.multimedia-gallery:first").attr("data-pmi"));
                    mmg.props.defaultGallery = false;

                    $("#sw-content-container10 .ui-widget.app.multimedia-gallery:first").csMultimediaGallery({
                        "efficientLoad" : true,
                        "imageWidth" : 1500,
                        "imageHeight" : 915,
                        "mobileDescriptionContainer": [768, 640, 480, 320], // [960, 768, 640, 480, 320]
                        "galleryOverlay" : false,
                        "linkedElement" : [], // ["image", "title", "overlay"]
                        "playPauseControl" : true,
                        "backNextControls" : true,
                        "bullets" : false,
                        "thumbnails" : false,
                        "thumbnailViewerNum": [4, 4, 3, 3, 2], // NUMERICAL - [960 view, 768 view, 640 view, 480 view, 320 view]
                        "autoRotate" : true,
                        "hoverPause" : true,
                        "transitionType" : "fade", // fade, slide, custom
                        "transitionSpeed" : 1.5,
                        "transitionDelay" : 4,
                        "fullScreenRotator" : true,
                        "fullScreenBreakpoints" : [960], // NUMERICAL - [960, 768, 640, 480, 320]
                        "onImageLoad" : function(props) {}, // props.element, props.recordIndex, props.mmgRecords
                        "allImagesLoaded" : function(props) {}, // props.element, props.mmgRecords
                        "onTransitionStart" : function(props) {}, // props.element, props.currentRecordIndex, props.currentGalleryIndex, props.nextRecordIndex, props.nextGalleryIndex, props.mmgRecords
                        "onTransitionEnd" : function(props) {}, // props.element, props.currentRecordIndex, props.currentGalleryIndex, props.mmgRecords
                        "allLoaded" : function(props) {

                            $("#hp-slideshow .mmg-container .mmg-control").not(".play-pause").addClass("icon-font-mmg-arrow");
