	$(document).ready(function() {
		var todayDate = Date.parse("today").toString();
		var checkDate = Date.parse("today").add(5).days(); 
		
		$("div.ui-article.menuitems").each(function() {
			var date = $.trim($(this).find("h1 span").html());
			var newDate = Date.parse(date).toString();
			var newDateEdited1 = Date.parse(date).toString("dddd");
			var newDateEdited2 = Date.parse(date).toString("dddd, MMMM d");
			
			if (newDate == todayDate) {
				$(this).find("h1 span").html("Today");
			} else {
                if (Date.parse(date).compareTo(Date.today()) == 1) {
                    if (Date.parse(date).compareTo(checkDate) == -1) {
                        $(this).find("h1 span").html(newDateEdited1);
                    } else {
                        $(this).find("h1 span").html(newDateEdited2);
                    }
                }
			}
		});
	});

