$(document).ready(function(){
   var beaconURL='https://analytics.schoolwires.com/analytics.asmx/Insert?AccountNumber=9zK0wKKLyyO2JTZ71up2QA%3d%3d&SessionID=3e58b067-f62b-4b0e-8575-a2d336162262&SiteID=4&ChannelID=0&SectionID=0&PageID=1&HitDate=4%2f19%2f2020+12%3a52%3a02+PM&Browser=Unknown+0.0&OS=Unknown&IPAddress=10.61.65.120';
