		function myInit(){

		$('#datepicker6 a.ui-datepicker-prev').keydown(function(e){
	    	if(e.which == 13){ // enter key
	    		e.preventDefault();
	    		$('#datepicker6 a.ui-datepicker-prev').click();
	    		handlePrevClick6();
	    	}
	    });
	    $('#datepicker6 a.ui-datepicker-next').keydown(function(e){
	    	if(e.which == 13){ // enter key
	    		e.preventDefault();
	    		$('#datepicker6 a.ui-datepicker-next').click();
	    		handleNextClick6();
	    	}
	    });


		function handlePrevClick6(){
			setTimeout(function () {
				triggerClick6();
			    $('#datepicker6').find('.ui-datepicker-prev').focus();
			}, 0);
		}
		function handleNextClick6(){
			setTimeout(function () {
				triggerClick6();
			    $('#datepicker6').find('.ui-datepicker-next').focus();
			}, 0);
		}
		function triggerClick6(){
			prev = $('#datepicker6').find('.ui-datepicker-prev');
			next = $('#datepicker6').find('.ui-datepicker-next');
			prev.attr('tabindex','0');
			next.attr('tabindex','0');

			$(next).on('click', handleNextClick6);
			$(prev).on('click', handlePrevClick6);
		}

		};



