	function jsMediaQueries(){
		switch(csGetBreakPoint()) {
			case "desktop": 
				mediaRotator();
				$(window).scroll(parallaxScroll);
				$("#hp-content-mid .ui-widget.app.headlines .ui-article").hover(

					function() {
						$(this).toggleClass("zoom");
						if($(".ui-article-inner.has-thumb", this).hasClass("active")){
							$(".ui-article-inner.has-thumb", this).removeClass("active");
						} else {
							$(".ui-article-inner.has-thumb", this).addClass("active");
						}
					}, function() {
						$(".ui-article-inner.has-thumb", this).removeClass("active");
						$(this).removeClass("zoom");
					}
				);
			break;
			case "768":
				mediaRotator768();
				appView();
			break;
			case "640":
				mediaRotator768();
				appView();
			break;
			case "480":
				mediaRotator768();
				appView();
				$("nav").addClass("drop-down");
			break;
			case "320":
				mediaRotator768();
				appView();
				$("nav").addClass("drop-down");
			break;
		}
	}

	function mediaRotator() {
		head.ready("mediaRotator", function(){
			var videoToggle = 'false';
			if(videoToggle == "false") {
				if(!$("#sw-content-container10.region.ui-hp .rs-gallery-container").length){
				        // MULTIMEDIA ROTATOR
				        $("#sw-content-container10.ui-hp div.ui-widget.app.cs-rs-multimedia-rotator").csRsMultimediaRotator({
				                "imageWidth"              : 1500,
				                "imageHeight"             : 915,
				                "efficientLoad"           : "yes",
				                "appHeader"               : "no",
				                "titles"                  : "yes",
				                "captions"                : "yes",
				                "galleryOverlay"          : "yes",
				                "linkedElement"           : ["image"], // ["image", "title", "overlay", "all"]
				                "playPause"               : "yes",
				                "mainControls"            : "no",
				                "contMoreLink"            : "no",
				                "contMoreLinkText"        : "Read More",
				                "descMoreLink"            : "yes",
				                "descMoreLinkText"        : "Read More",
				                "bullets"                 : "yes",
				                "bulletControls"          : "no",
				                "thumbnails"              : "no",
				                "thumbnailWidth"          : 122,
				                "thumbnailHeight"         : 53,
				                "thumbnailSlideDirection" : "horizontal", // horizontal or vertical
				                "hoverPause"              : "yes",
				                "autoRotate"              : "yes",
				                "fullScreenRotator"       : "yes", // image dimensions work well at 1500x915
				                "fullScreenBreakpoints"   : ["960"], // OPTIONS ARE ["960", "768", "640", "480", "320"]
				                "fullScreenVideo"         : "no",
				                "transitionType"          : "fade", // fade or slide
				                "transitionSpeed"         : 3,
				                "transitionDelay"         : 8,
				                "onImageLoad"             : function() { },
				                "allImagesLoaded"         : function() { },
				                "onThumbnailLoad"         : function() { },
				                "allThumbnailsLoaded"     : function() { },
				                "onTransitionStart"       : function() { },
				                "onTransitionEnd"         : function() { },
				                "watchVidClick"           : function() { },
				                "closeVidClick"           : function() { },
				                "allLoaded"               : function() { 
                                	$('#hp-slideshow .rs-gallery-desc').wrap('
');

                                	if( $('#hp-slideshow .rs-gallery-control.play-pause').length ){
                                    	$('#hp-slideshow .rs-gallery-bullets-outer').append($('#hp-slideshow .rs-gallery-control.play-pause'));
                                    	$('#hp-slideshow .rs-gallery-desc-wrapper').append($('#hp-slideshow .rs-gallery-bullets-outer'));
                                    }
                                }
				        });
				}
			} else {
				$("#hp-slideshow #sw-content-container10").hide(); 
				$(".mobile #hp-header-outer").hide();
				$("#hp-slideshow #hp-player").show();
				$("#hp-slideshow #player").show();
				$("#play-pause-outer").addClass('show');
			}	
		});
	}

