    function stickyCalendarFix() {
    	var headerHeight = $("#gb-mystart-outer").height();

        if ($(window).scrollTop() <= headerHeight) {

            if($(".sp").length && $(".ui-widget.app.calendar").length) {
                $(".wcm-controls").css("margin-top", "0px");
            }
        } else {

            if($(".sp").length && $(".ui-widget.app.calendar").length) {
                if($(".wcm-controls").hasClass("wcm-stuck")) {
                    $(".wcm-controls").css("margin-top", headerHeight + "px");
                }
            }
        }
    }

	function searchText() {
		//CUSTOM MYSTART SEARCH PLACEHOLDER
	        //SEARCH TEXT
	        $("div#sw-mystart-search input#sw-search-input").removeAttr("placeholder");
		$("div#sw-mystart-search input#sw-search-input").attr("value", "Searching for");
		$("div#sw-mystart-search input#sw-search-input").focus(function() {
			if($(this).val() == "Searching for") {
				$(this).val("");
			}
		});
		$("div#sw-mystart-search input#sw-search-input").blur(function() {
			if($(this).val() == "") {
				$(this).val("Searching for");
			}
		});
	}

	function modEvents(){
		head.ready("modEvents", function() {
			if (!$(".upcomingevents .joel-month").length) {
				$(".upcomingevents").modEvents({
					columns     : "yes",
					monthLong   : "no",
					dayWeek     : "yes"
				});
				eventsByDay(".upcomingevents .ui-articles");
			}
		});
	}		

	function eventsByDay(container) {
		//MOVE ALL EVENTS WITH THE SAME DATES INTO THE SAME CONTAINER
		var countLi = 0;
		$(".ui-article", container).each(function(){
			//$("h1 .joel-day", this).prependTo(".ui-article-description .sw-calendar-block-time", this);
			if (!$(this).find("h1.ui-article-title.sw-calendar-block-date").size()){
				var moveArticle = $(this).html();
				$(this).parent().prev().children().children().next().append(moveArticle);
				$(this).parent().remove();

			};

		});	

		$(".ui-article", container).each(function(){
			if($(".ui-article-description .sw-calendar-block-time", this).length) {
				$("h1 .joel-day", this).html($("h1 .joel-day", this).text().substr(0, 3)).prependTo($(".ui-article-description .sw-calendar-block-time", this));
			} else {
				$("h1 .joel-day", this).hide();
			}
		});

        //LIMIT EVENTS ON HOMEPAGE
		$(".hp.column.one .upcomingevents li:gt(4)").remove();


	}

	function calendarListView(){
		//AUTO CLICK FUNCTION FOR SCHOOLWIRES CALENDAR LIST VIEW FOR SMALL MOBILE DEVICES
		if(!$("a.ui-btn-toggle.list").hasClass("active")){
			$("a.ui-btn-toggle.list").click();
		}
	}

	function socialIconsNew() {
		$("#gb-social .icn").each(function(){

      			$(this).off().hover(function(){

      				if($(this).hasClass("closed")) {
      					var gbTextWidth = $(".icon-text",this).width();
					$(".icon-text",this).stop(true,true).animate({
						width: gbTextWidth + 54

					}, 400, 'swing')
					$(this).removeClass("closed").addClass("open");		

				} else {
					$(".icon-text",this).animate({
						width: '0px'

				    	}, 400)
					$(this).removeClass("open").addClass("closed");		
				}
      			});
      		});
	}

	var parallaxScroll = function(){
		var getWidth = $(window).width();
		var slideshowHeight = ($("#hp-slideshow").height() - 60);
		var headerHeight = $("#gb-header-outer").height();
		if (getWidth >= 1024) { // 960+
			if($(".hp").length) {
				if($(window).scrollTop() >= slideshowHeight) {
					$("#gb-mystart-outer").addClass("fixed");
					$("main").css("margin-top", "130px");
				} else {
					$("#gb-mystart-outer").removeClass("fixed");
					$("main").css("margin-top", "0px");
				}
			} else {
				if($(window).scrollTop() >= headerHeight) {
					$("#gb-mystart-outer").addClass("fixed");
					$("main").css("margin-top", "100px");
				} else {
					$("#gb-mystart-outer").removeClass("fixed");
					$("main").css("margin-top", "0px");
				}
			}
		}
		// SET CLASS TO MAKE DROPDOWNS DROP UP IF SCROLL POSITION IS LESS THAN 40 PIXELS FROM TOP EDGE OF WINDOW

			if ($(window).scrollTop() >= 180) {
				$("nav").addClass("drop-down");
			} else {
				$("nav").removeClass("drop-down");
			}

	}

	function csGetBreakPoint() {
		return window.getComputedStyle(document.querySelector("body"), ":before").getPropertyValue("content").replace(/"|'/g, "");/*"*/
	}

