function Translate(lang) {
		var subdomainParts = location.hostname.split(".");
		var domain = subdomainParts[subdomainParts.length-2] + "." + subdomainParts[subdomainParts.length-1];
		var cookieVal = (lang == 'en') ? '' : '/en/' + lang;
		document.cookie = "googtrans=" + cookieVal + ";domain=" + domain + ";path=/;";
		document.cookie = "googtrans=" + cookieVal + ";domain=." + domain + ";path=/;";
		document.cookie = "googtrans=" + cookieVal + ";domain=" + domain + ";";
		document.cookie = "googtrans=" + cookieVal + ";domain=." + domain + ";";
		document.cookie = "googtrans=" + cookieVal + ";path=/;";
		document.cookie = "googtrans=" + cookieVal + ";";
		 window.location.assign(window.location.href.replace(window.location.hash,''));
		 window.location.reload(true);
		 // window.location.hash ="#googtrans/en/"+lang;
	}

function googleTranslateElementInit() {
 new google.translate.TranslateElement({pageLanguage: 'en', layout: google.translate.TranslateElement.InlineLayout.SIMPLE}, 'google_translate_element');
}
$(".langbar-select").on("change", function() {
	Translate($(this).val());
});

