		switch(disclaimerToggle) {
			case"false":
				$("#footer-disclaimer").hide();
			break;
			case"true":
				$("#footer-disclaimer").show();
			break;
		}

		// PHOTO GALLERY CONFIGURATION
		//DOC #sw-content-container -- INITIALIZE PHOTOGALLERY
		if($("#sw-content-container13.region.ui-hp div.ui-widget.app.gallery").size()) {
			$("#sw-content-container13.ui-hp div.ui-widget.app.gallery").creativeGalleryRotateSimple({
				"imageHeight" : 240,
				'imageControls': "off",
				"useOverlay" : "off",
				'randomOrder': "off",
				"animateType" : "fade"
			}); 
		} else {
			$("#hp-slideshow").css("background", "url(/cms/lib/NY01000156/Centricity/Template/4//static/default.jpg)");
		}
		// DRAW CANVAS CURVE
		if(LTIE9 != 1) {
			drawCanvas();
		} else {

		}

		$("#hp-content .ui-widget-header h1").each(function() {
			if($(this).text() == "") {
				$(this).css("display" , "none");
			}
		});
	});
	function useChannelNoSection(){
		//DOC .ui-widget-header h1, .ui-widget-header -- SET USE ChannelName FOR THE PAGELISTNAVIGATION HEADER IF ONE IS NOT SET
		if(!$("div.sp.column.one .ui-widget-header h1").size()) {
        		$("div.sp.column.one .ui-widget-header").append("
");
        	}

	}	
	function dynamicContent() {
		//DOC fixes search box not allowing spaces
		$("#sw-mystart-search").unbind(); 

		//DOC #swsignin-txt-username -- AUTO-FOCUS SIGNIN FORM FIELD #swsignin-txt-username
		$("#swsignin-txt-username").focus();

		//DOC .sw-mystart-button.home -- ELEMENT districtButton SHOWS OR HIDES DISTRICT HOME BUTTON
		var districtButton = 'true';
		if(districtButton == "false"){
			$(".sw-mystart-button.home").hide();
		}
		//DOC #sp-breadcrumbs -- ELEMENT breadcrumbs SHOWS OR HIDES BREADCRUMBS
		var breadcrumbs = 'true';
		if(breadcrumbs == "false"){
			$("#sp-breadcrumbs, #spn-breadcrumbs").hide();
		}
		// #gb-logo -- ELEMENT showSchoolLogo SHOWS OR HIDES SCHOOL LOGO
		var showLogo = 'false'; 
		if(showLogo == "true") {
			$("#gb-logo").html('');
		}		
	}
	function sameHeight(selector) {
		selector.height(function(){
			$(this).height($(this).parent().height());
		});
		// IF .children() OF SELECTOR RESIZE THEN RESET HEIGHT OF SELECTOR
		selector.children().resize(function(){
			selector.height("auto");
			selector.height(function(){
				$(this).height($(this).parent().height());
			});
		});
	}

