	});	

	function pgScreenSize(){
		var getWidth = $(window).width();
		if (getWidth >= 960) { // 960+
			photoGallery();
			schoolListMod();
			resize768Above = true;
			resize640Under = false;
			sameHeightHome(resize768Above, resize640Under);
			responsiveHeadlinesSlider();
			if(!isMSIE8){ slideshowPopOutInit(); headlinesLeftPopOutinit(); headlinesRightPopOutinit(); }
			addFramesetTranslate();
		} if (getWidth >= 768 && getWidth < 960) { // 768+
			photoGallery();
			resize768Above = true;
			resize640Under = false;
			sameHeightHome(resize768Above, resize640Under);
			responsiveHeadlinesSlider();
			moveGlobals();
			addChannelSearch();
			if(!isMSIE8){ headlinesLeftPopOutinit(); headlinesRightPopOutinit(); }
		} if (getWidth >= 640 && getWidth < 768) { // 640+
			photoGallery();
			resize768Above = false;
			resize640Under = true;
			sameHeightReset(resize768Above, resize640Under);
			responsiveHeadlinesSlider();
			appView();
			moveGlobals();
			if(!isMSIE8){ headlinesLeftPopOutinit(); headlinesRightPopOutinit(); }
		} if (getWidth >= 480 && getWidth < 640) { // 480+
			photoGallery();
			calendarListView();
			resize768Above = false;
			resize640Under = true;
			sameHeightReset(resize768Above, resize640Under);
			responsiveHeadlinesSlider();
			appView();
			moveGlobals();
			if(!isMSIE8){ headlinesLeftPopOutinit(); headlinesRightPopOutinit(); }
		} if (getWidth >= 320 && getWidth < 480) { // 320+
			photoGallery();
			calendarListView();
			appView();
			moveGlobals();
			if(!isMSIE8){ headlinesLeftPopOutinit(); headlinesRightPopOutinit(); }
		}
	}

	function photoGallery(){
		if(!$("#sw-content-container10.region.ui-hp .gallery-container").length){
			// PHOTO GALLERY CONFIGURATION
			$("#sw-content-container10.ui-hp div.ui-widget.app.gallery").creativeGalleryRotate({
				"imageHeight"		: 100,
				"imageControls"		: "on",
				"animateType"		: "fade", // CHOICES - "slide"
				"animateSpeed"		: 3,
				"pauseHover"		: ['all'], // CHOICES - ['title'], ['overlay'], ['caption'], ['all']
				"autoRotate"		: "on",
				"useTitles"		: "on",
				"useCaptions"		: "on",
				"useOverlay"		: "off",
				"randomOrder"		: "off",
				"linkedElement"		: ['overlay','picture'], // CHOICES - ['picture'], ['title'], ['overlay']
				"itemizedBullets"	: "off",
				"thumbnails"		: "off",
				"thumbnailWidth"	: 50,
				"thumbnailHeight"	: 32,
				"thumbnailControls"	: "off",
				"thumbnailLimit"	: 3,
				"addClass"		: "unclassed",
				"moreLinkText"		: "off" 
			});
		}
	}

	function appView(){
		if(!$("div.ui-widget.app.active").length){
			if($(".ui-widget.app.gallery").is(":first")){
				$("#hp-content div.ui-widget.app").eq(1).addClass("active");
			} else {
				$("#hp-content div.ui-widget.app:first").addClass("active");
			}
		}
		$("div.ui-hp div.ui-widget-header").click(function(){
			$("div.ui-widget.app").removeClass("active");
			$(this).parent().addClass("active");
		});
	}	

	function useChannelForSection(){
		if(!$("div.sp.column.one .ui-widget-header h1").length > 0) {
			$("div.sp.column.one .ui-widget-header").append("
");
		}
        }

	function dynamicContent(){
		// ********** MYSTART ********** //
		$(".sw-myaccount-chevron").text("");
		$(".sw-dropdown-arrow").text("");
		if(!district){
			$(".sw-mystart-button.home a span").html("fCCS Home");
			$("#sw-mystart-left").append($(".sw-mystart-button.home").show());
		}
		$(".sw-mystart-dropdown.sort .selector").text("Our Schools...");

        //$("a[href='/Domain/20']").attr('href', '/Domain/12');	/*Added by Lyndsay - Case 00328906 */

		// ********** HEADER ********** //
		// ADD DISTRICT SITE ALIAS CLASS IF ON THE DISTRICT ELSE SHOW THE SMALLER DISTRICT LOGO
		if(district){
			$("#gb-page").addClass("ccs");
		} else {
			$("#gb-subsite-district-logo").show();
		}

		// SET THE HEADER WATERMARK IMAGE IF IT EXISTS
		$.ajax({
			type: "GET",
			url: "/site/handlers/getimagesxml.ashx",
			data: "SiteID=4&base_folder=template-assets&folderName=watermark",
			dataType: "xml",
			cache: false,
			success: function(data){
				var imgNum = $(data).find("img").length;
				if(imgNum > 0){
					var imgPath = "/cms/lib/NC01910456/Centricity/Domain/4/template-assets/watermark/" + $(data).find("img").eq(0).attr("src");
					$("#gb-header").css("background", "url(" + imgPath + ") no-repeat right top");
				}
			}
		});

		// SET THE LOGO IF IT EXISTS
		if("ccs" == "ccs"){
			$("#gb-logo a").append("");
		} else {
			$.ajax({
				type: "GET",
				url: "/site/handlers/getimagesxml.ashx",
				data: "SiteID=4&base_folder=template-assets&folderName=logo",
				dataType: "xml",
				cache: false,
				success: function(data){
					var imgNum = $(data).find("img").length;
					if(imgNum > 0){
						var imgPath = "/cms/lib/NC01910456/Centricity/Domain/4/template-assets/logo/" + $(data).find("img").eq(0).attr("src");
						$("#gb-logo a").append("");
						$("#gb-logo img").show();
					} else {
						$("#gb-logo").hide();
					}
				}
			});
		}

		// SPLIT THE SITENAME INTO TWO LINES IF NOT ON THE DISTRICT
		if(!district){
			var wrapNum = '0';
			if(wrapNum == "0;1;2;3"){
				wrapNum = 1;
			}
			var siteName = "Cabarrus County Schools";
			siteName = siteName.split(" ");
			siteNameEnd = jQuery.trim(siteName.splice(-wrapNum, siteName.length).toString().replace(/,/g, " "));
			siteNameBegin = jQuery.trim(siteName.toString().replace(/,/g, " "));
			$("#gb-sitename").prepend("
" + siteNameEnd + "");
			$("#gb-sitename").prepend("
" + siteNameBegin + "");
		}
		$("#gb-logo img").show();

		// ********** CHANNEL BAR ********** //
		$("li.sw-channel-item").last().addClass("last");
		// ADD THE HOME AND SEARCH BUTTONS TO THE CHANNEL BAR
		$("#channel-navigation").prepend("
d
e
");
		$("#sw-search-button").html("e");
		$("#gb-channel-bar-search").append($("#sw-mystart-search").show());
		$("li.gb-channel-item.search").click(function(e){
			e.preventDefault();
			$("#gb-channel-bar-search").slideToggle();
		});

		// ********** BODY ********** //
		$("li.ui-breadcrumb-first > a > span").text("Home");
		$("ul.ui-breadcrumbs > li").not($("ul.ui-breadcrumbs > li.ui-breadcrumb-last")).append(" > ");
		$("a.more-link span").text("More ");
		$("#swsignin-txt-username").focus();
        	if($("div.ui-spn > div > p > span").text() == "You have been signed out."){
        		$("div.ui-spn > div").css({"text-align" : "center", "padding" : "50px 0px 50px 0px"});
        	}

        	// SET CUSTOM BACKGROUND IMAGE
        	var bgColor = '#0099CC';
        	$("").attr("src", "/cms/lib/NC01910456/Centricity/template/11/background-images/ccs.png").load(function() {
			$("#gb-content-outer").css("background", "url(/cms/lib/NC01910456/Centricity/template/11/background-images/ccs.png) no-repeat center top " + bgColor);
			$("#gb-footer-outer-bg").css("background", "url(/cms/lib/NC01910456/Centricity/template/11/background-images/ccs.png) no-repeat center top");
		}).error(function(){
			$("#gb-content-outer").css("background", "url(/cms/lib/NC01910456/Centricity/template/11/background-images/ccs.png) no-repeat center top " + bgColor);
			$("#gb-footer-outer-bg").css("background", "url(/cms/lib/NC01910456/Centricity/template/11/background-images/ccs.png) no-repeat center top");
		});

        	// HIDE GALLERY DESCRIPTION BACKGROUND CORNER IF COLOR IS TRANSPARENT
        	var descColor = '';
        	descColor = descColor.indexOf("#");
        	if(descColor == -1){
        		$("#slideshow-pop-out-left-container").hide();
        	}

        	// TURN ON THE HEADLINES SLIDER APP IF SET TO SHOW
        	var headlinesSliderSwitch = 'Show';
        	if(headlinesSliderSwitch == 'Show' || headlinesSliderSwitch == 'Show;Hide'){
        		$("#hp-headlines").show();
        	}

        	// TURN ON THE WELCOME APP IF SET TO SHOW
        	var welcomeSwitch = 'Show';
        	if(welcomeSwitch == 'Show' || welcomeSwitch == 'Show;Hide'){
        		$("#welcome").show();
        	}

		// ********** FOOTER ********** //
		// SCROLL TO TOP BUTTON
		$("#gb-footer-to-top a").click(function(e){
			e.preventDefault();
			$("html, body").animate({ scrollTop: 0 }, "slow");
			return false;
		});
		var feedbackURL = $("div#sw-footer-links li:eq(0) a").attr("href");
		var termsURL = $("div#sw-footer-links li:eq(2) a").attr("href");
		var policyURL = $("div#sw-footer-links li:eq(1) a").attr("href");

		$(".schoolwires-links").each(function(){
			$(this).find("a.feedback").attr("href", feedbackURL);
			$(this).find("a.terms").attr("href", termsURL);
			$(this).find("a.policy").attr("href", policyURL);
		});		
	}

	function searchText() {
	        //SEARCH TEXT
	        $("div#sw-mystart-search input#sw-search-input").removeAttr("placeholder");
		$("div#sw-mystart-search input#sw-search-input").attr("value", "Search...");
		$("div#sw-mystart-search input#sw-search-input").focus(function() {
			if($(this).val() == "Search...") {
				$(this).val("");
			}
		});
		$("div#sw-mystart-search input#sw-search-input").blur(function() {
			if($(this).val() == "") {
				$(this).val("Search...");
			}
		});
	}

	function calendarListView(){
		if(!$("a.ui-btn-toggle.list").hasClass("active")){
			$("a.ui-btn-toggle.list").click();
		}
	}

	function imgResize(){
		var imgHeight = $("#hp-slideshow .rotating-images img").height();
		if(imgHeight > 407){
			imgHeight = 407;
		}
		$("#hp-slideshow, #hp-slideshow .rotating-images ul.pictures li").height(imgHeight);
	}

	function sameHeightHome(resize768Above, resize640Under){
		// DEFINE VARIABLES
		var hpContentHeight = 0, hpColOneHeight = 0, hpColTwoHeight = 0, hpColOneBottomHeight = 0, hpColTwoAppHeight = 0, hpColOneBottomLeftHeight = 0, hpColOneBottomRightHeight = 0, hpColOneBottomAppHeight = 0;

		// HEIGHT RESET
		$(".hp.one, .hp.two, #hp-col-one-bottom").height("auto");
		$(".hp.two .ui-widget-detail").last().height("auto");
		$(".hp-col-one-bottom.left .ui-widget-detail").last().height("auto");
		$(".hp-col-one-bottom.right .ui-widget-detail").last().height("auto");

		// SET UP THE FIRST OR OUR VARIABLES
		hpContentHeight = $("#hp-content").height();
		hpColOneHeight = $(".hp.one").height();
		hpColTwoHeight = $(".hp.two").height();

		// CHECK WHICH COLUMN IS HIGHER AND ADJUST COLUMNS ACCORDINGLY
		if(hpColOneHeight > hpColTwoHeight){
			$(".hp.two").height(hpContentHeight);
			hpColTwoHeight = hpContentHeight;

			// GET THE HEIGHT OF ALL APPS EXCEPT THE LAST ONE
			$(".hp.two .ui-widget.app").not($(".hp.two .ui-widget.app").last()).each(function(){
				hpColTwoAppHeight += $(this).height() + 10;
			});

			// GET THE HEIGHT OF THE APP DETAIL AND FOOTER OF THE LAST APP
			hpColTwoAppHeight += ($(".hp.two .ui-widget-header").last().height() + $(".hp.two .ui-widget-footer").last().height());
			hpColTwoAppHeight = hpContentHeight - hpColTwoAppHeight;

			// SET THE HEIGHT TO THE LAST APP DEATAIL
			$(".hp.two .ui-widget-detail").last().height(hpColTwoAppHeight - 35);
		}

		// SET UP THE REMAINDER OF OUR VARIABLES
		hpColOneBottomHeight = hpContentHeight - ($("#hp-slideshow").height() + $("#welcome").height() + 10);
		$("#hp-col-one-bottom").height(hpColOneBottomHeight);

		hpColOneBottomLeftHeight = $(".hp-col-one-bottom.left").height();
		hpColOneBottomRightHeight = $(".hp-col-one-bottom.right").height();

		// IF INNER COLUMN ONE IS HIGHER THAN INNER COLUMN TWO
		if(hpColOneBottomLeftHeight > hpColOneBottomRightHeight){
			// GET THE HEIGHT OF ALL APPS EXCEPT THE LAST ONE
			$(".hp-col-one-bottom.right .ui-widget.app").not($(".hp-col-one-bottom.right .ui-widget.app").last()).each(function(){
				hpColOneBottomAppHeight += $(this).height() + 10;
			});

			// GET THE HEIGHT OF THE APP DETAIL AND FOOTER OF THE LAST APP
			hpColOneBottomAppHeight += ($(".hp-col-one-bottom.right .ui-widget-header").last().height() + $(".hp-col-one-bottom.right .ui-widget-footer").last().height());
			hpColOneBottomAppHeight = hpColOneBottomHeight - hpColOneBottomAppHeight;

			// SET THE HEIGHT TO THE LAST APP DEATAIL
			$(".hp-col-one-bottom.right .ui-widget-detail").last().height(hpColOneBottomAppHeight - 35);
		} else {
			// GET THE HEIGHT OF ALL APPS EXCEPT THE LAST ONE
			$(".hp-col-one-bottom.left .ui-widget.app").not($(".hp-col-one-bottom.left .ui-widget.app").last()).each(function(){
				hpColOneBottomAppHeight += $(this).height();
			});

			// GET THE HEIGHT OF THE APP DETAIL AND FOOTER OF THE LAST APP
			hpColOneBottomAppHeight += ($(".hp-col-one-bottom.left .ui-widget-header").last().height() + $(".hp-col-one-bottom.left .ui-widget-footer").last().height());
			hpColOneBottomAppHeight = hpColOneBottomHeight - hpColOneBottomAppHeight;

			// SET THE HEIGHT TO THE LAST APP DEATAIL
			$(".hp-col-one-bottom.left .ui-widget-detail").last().height(hpColOneBottomAppHeight - 35);
		}

		// IF BROWSER IS AT 768+ AND IF THE RESIZE EVENTS HAVENT BEEN BOUND YET
		if(resize768Above == true && heightSet == false){
			$(window).bind("resize.homeResize", sameHeightHome);
			$(".hp.column .ui-widget.app .ui-widget-detail").resize(function(){ sameHeightHome(); });

			heightSet = true;
			heightReset = false;
		}
	}

	function sameHeightReset(resize768Above, resize640Under){
		// IF BROWSER IS AT 640- AND IF THE RESIZE EVENTS HAVENT BEEN UNBOUND YET
		if(resize640Under == true && heightReset == false){
			$(window).unbind("resize.homeResize", sameHeightHome);
			$(".hp.column .ui-widget.app .ui-widget-detail").resize(function(){ sameHeightReset(); });

			heightReset = true;
			heightSet = false;
		}

		// RESET THE HEIGHTS
		$(".hp.column.one, .hp.column.two, #hp-col-one-bottom").height("auto");
		$(".hp.column.two .ui-widget-detail").last().height("auto");
		$(".hp-col-one-bottom.left .ui-widget-detail").last().height("auto");
		$(".hp-col-one-bottom.right .ui-widget-detail").last().height("auto");
	}

	function modEvents(){
		if (!$(".upcomingevents .joel-month").length) {
			$(".upcomingevents").modEvents({
				columns:"yes"
			});
			//addDateBlocks();
			eventsByDay(".ui-hp .upcomingevents .ui-articles");
		}
	}

	function eventsByDay(container) {
		var countLi = 0;
		$(".ui-article", container).each(function(){
			if (!$(this).find("h1.ui-article-title.sw-calendar-block-date").size()){
				var moveArticle = $(this).html();
				$(this).parent().prev().children().children().next().append(moveArticle);
				$(this).parent().remove();
			};
		});
	}	

	function addDateBlocks(){
		if($("div.ui-hp div.upcomingevents span.sw-calendar-block-title").length){
			$("div.ui-hp div.upcomingevents div.ui-article p.ui-article-description").not("div.ui-article.upcoming-column-container p.ui-article-description").each(function(){
				$(this).wrap("
");
				$(this).parent().parent().addClass("ui-clear upcoming-column-container");
				var monthText = $(this).parent().parent().parent().prev().find("div.upcoming-column.left").html();
				$(this).parent().parent().prepend("
" + monthText + "");
			});
		}

	}

	function schoolListMod(){
		if(!$("#gb-schoollist .sw-school-list").length){
			// SET UP OUR VARIABLES
			var elementarySchools = "", middleSchools = "", highSchools = "", nonTraditionalSchools = "";

			// LOOP THROUGH THE SCHOOL LIST
			$(".schoollist .sw-dropdown-list li").each(function(){
				if($("a", this).text().indexOf("Elementary") !== -1 || $("a", this).text().indexOf("Odell Primary") !== -1){
					elementarySchools += "
" + $(this).html() + "";
				} else if($("a", this).text().indexOf("Middle") !== -1){
					middleSchools += "
" + $(this).html() + "";
				} else if($("a", this).text().indexOf("High") !== -1){
					highSchools += "
" + $(this).html() + "";
				} else {
					nonTraditionalSchools += "
" + $(this).html() + "";
				}
			});

			//SCHOOL LIST STRUCTURE
			$("#gb-schoollist").append("" +
				"
" +
					"
" +
						"
Elementary" +
						"" +
							elementarySchools +
						"" +
					"" +
					"
" +
						"
Middle" +
						"" +
							middleSchools +
						"" +
					"" +
					"
" +
						"
High" +
						"" +
							highSchools +
						"" +
					"" +
					"
" +
						"
Non-Traditional" +
						"" +
							nonTraditionalSchools +
						"" +
					"" +
				"" +
			"");

			// SPLIT THE ELEMENTARY SCHOOLS INTO TWO COLUMNS
			var elemSchools = $(".school-list.elementary li").length;
			var count = Math.round(elemSchools / 2);
			$("

Elementary Two").insertAfter($(".school-container.elementary"));
			for(var i = 1; i <= count; i++){
				$(".school-list.elementary.two").append($(".school-list.elementary li").eq(9));
			}

			$(".sw-mystart-button.home").after("

Select a School...
");

			// TOGGLE THE CUSTOM SCHOOL LIST ON CLICK
			$(".sw-mystart-dropdown.sort").click(function(e){
				e.preventDefault();
				$("#gb-schoollist").slideToggle(function(){
					if(!$(".sw-mystart-dropdown.sort").hasClass("open")){
						$(".sw-mystart-dropdown.sort").removeClass("closed").addClass("open");
						$(this).removeClass("closed").addClass("open");
					} else {
						$(".sw-mystart-dropdown.sort").removeClass("open").addClass("closed");
						$(this).removeClass("open").addClass("closed");
					}
					$(this).removeAttr("style");
				});
			});
		}
	}

	function moveGlobals(){
		if(!$("#channel-global-icons").length){
			$("#gb-global-icons").clone().attr("id", "channel-global-icons").appendTo($("#gb-channel-list").append("
Popular Links >"));
		}
	}

	function addChannelSearch(){
		if(!$("#gb-channel-right-search").length){
			$("#sw-mystart-search").clone().attr("id", "gb-channel-right-search").removeAttr("style").addClass("hide960 show768 hide640 ie-pie").appendTo($("#gb-channel-list"));
			$("#gb-channel-right-search #sw-search-input").attr("id", "gb-search-input");
			$("#gb-channel-right-search #sw-search-button").attr("id", "gb-search-button");
			$("#gb-search-button").html("e");
		}
	}

	function responsiveHeadlinesSlider(){
		var headlinesSliderSwitch = 'Show';
        	if(headlinesSliderSwitch == 'Show'){
			if(!$("#hp-headlines .ui-widget.app.headlines #hp-headlines-slide-container").length){
				// SET UP OUR VARIABLES
				var headlineNum = $("#hp-headlines .ui-widget.app.headlines .ui-articles li").length;
				var headlineGroups = Math.ceil(headlineNum / 4);
				var slideNum = 0, viewerHeight = 0, groupClass = "", itemClass = "";

				// PREPEND THE VIEWER CONTAINER TO HEADLINES DETAIL
				$("#hp-headlines .ui-widget.app.headlines .ui-widget-detail").prepend("

");

				// INSTANTIATE THE NEXT AND BACK CLICK EVENTS
				$("#hp-headlines .slider-button.previous").live("click", function(){
					// IF THE SLIDER IS NOT CURRENTLY SLIDING
					if(!$(".hp-slide-group.animating").length){
						// IF THE FIRST SLIDE GROUP IS NOT THE ACTIVE GROUP
						if(!$(".hp-slide-group.active").hasClass("first")){
							// GIVE THE SLIDE CONTAINER A HEIGHT FOR THE TIME BEING
							viewerHeight = $("#hp-headlines-slide-container").height();
							$("#hp-headlines-slide-container").height(viewerHeight);

							// SET THE APPROPRIATE CLASSES
							$(".hp-slide-group.active").prev().addClass("animating right");
							$(".hp-slide-group.active").addClass("animating-out right");

							// SLIDE OUT THE ACTIVE AND SLIDE IN THE NEW GROUP SIMULTANEOUSLY
							$(".hp-slide-group.animating-out.right").animate({
								right: "-100%"
							}, 2000, function(){
								// REMOVE CONTAINER HEIGHT AND RESET CLASSES ACCORDINGLY
								$(".hp-slide-group.animating-out.right").removeAttr("style").removeClass("animating-out right active");
							});
							$(".hp-slide-group.animating.right").animate({
								right: "0%"
							}, 2000, function(){
								// REMOVE CONTAINER HEIGHT AND RESET CLASSES ACCORDINGLY
								$(".hp-slide-group.active").removeClass("active");
								$(".hp-slide-group.animating.right").removeAttr("style").removeClass("animating right").addClass("active");
								$("#hp-headlines-slide-container").height("auto");
							});
						}
						return false;
					}
					return false;
				});

				$("#hp-headlines .slider-button.next").live("click", function(){
					// IF THE SLIDER IS NOT CURRENTLY SLIDING
					if(!$(".hp-slide-group.animating").length){
						// IF THE LAST SLIDE GROUP IS NOT THE ACTIVE GROUP
						if(!$(".hp-slide-group.active").hasClass("last")){
							// GIVE THE SLIDE CONTAINER A HEIGHT FOR THE TIME BEING
							viewerHeight = $("#hp-headlines-slide-container").height();
							$("#hp-headlines-slide-container").height(viewerHeight);

							// SET THE APPROPRIATE CLASSES
							$(".hp-slide-group.active").next().addClass("animating left");
							$(".hp-slide-group.active").addClass("animating-out left");

							// SLIDE OUT THE ACTIVE AND SLIDE IN THE NEW GROUP SIMULTANEOUSLY
							$(".hp-slide-group.animating-out.left").animate({
								left: "-100%"
							}, 2000, function(){
								// REMOVE CONTAINER HEIGHT AND RESET CLASSES ACCORDINGLY
								$(".hp-slide-group.animating-out.left").removeAttr("style").removeClass("animating-out left active");
							});
							$(".hp-slide-group.animating.left").animate({
								left: "0%"
							}, 2000, function(){
								// REMOVE CONTAINER HEIGHT AND RESET CLASSES ACCORDINGLY
								$(".hp-slide-group.active").removeClass("active");
								$(".hp-slide-group.animating.left").removeAttr("style").removeClass("animating left").addClass("active");
								$("#hp-headlines-slide-container").height("auto");
							});
						}
						return false;
					}
					return false;
				});

				// LOOP THROUGH THE HEADLINES AS MANY TIMES AS THERE ARE HEADLINES
				for(var i = 1; i <= headlineGroups; i++){
					groupClass = "";

					// SET SOME HANDY CLASSES
					if(i == 1){
						groupClass = " first";
					} else if(i == headlineGroups){
						groupClass = " last";
					}

					// APPEND THE GROUP
					$("#hp-headlines-slide-container").append("
");

					// ONLY 4 HEADLINES ARE IN A GROUP SO ONLY LOOP 4 TIMES WITHIN EACH OUTER LOOP
					for(var j = 1; j <= 4; j++){
						// IF THE LOOP IS ALL THE WAY THROUGH THE HEADLINES THEN TERMINATE THIS SCRIPT
						if(slideNum >= headlineNum){
							$(".slide-group-1").addClass("active");
							return false;
						} else {
							itemClass = "";

							// SET UP SOME HANDY CLASSES
							if(j == 1){
								itemClass = " first";
							} else if(j == 4){
								itemClass = " last";
							}

							// APPEND THE SLIDE
							$("#hp-headlines-slide-container .slide-group-" + i).append("
" + $("#hp-headlines .ui-widget.app.headlines .ui-articles li").eq(slideNum).clone().html() + "");
							slideNum++;
						}
					}
				}
			}
		}
	}

