	function slideshowPopOutInit() {
		if($(".hp").length){
			var canvas = document.getElementById("slideshow-pop-out-left");
			if (typeof(G_vmlCanvasManager) != "undefined"){G_vmlCanvasManager.initElement(canvas);}
			var ctx = canvas.getContext("2d");

			drawSlideshowPopOut(ctx);
		}
	}

	function drawSlideshowPopOut(ctx) {
		// layer1/Path
		ctx.save();
		ctx.beginPath();
		ctx.moveTo(0.0, 0.0);
		ctx.lineTo(10.0, 15.0);
		ctx.lineTo(10.0, 0.0);
		ctx.lineTo(0.0, 0.0);
		ctx.closePath();
		ctx.fillStyle = '';
		ctx.fill();
		ctx.restore();
	}

	function headlinesLeftPopOutinit() {
		if($(".hp").length){
			var canvas = document.getElementById("headlines-pop-out-left");
			if (typeof(G_vmlCanvasManager) != "undefined"){G_vmlCanvasManager.initElement(canvas);}
			var ctx = canvas.getContext("2d");

			drawHeadlinesLeftPopOut(ctx);
		}
	}

	function drawHeadlinesLeftPopOut(ctx) {
		// layer1/Path
		ctx.save();
		ctx.beginPath();
		ctx.moveTo(0.0, 0.0);
		ctx.lineTo(10.0, 15.0);
		ctx.lineTo(10.0, 0.0);
		ctx.lineTo(0.0, 0.0);
		ctx.closePath();
		ctx.fillStyle = '';
		ctx.fill();
		ctx.restore();
	}

	function headlinesRightPopOutinit() {
		if($(".hp").length){
			var canvas = document.getElementById("headlines-pop-out-right");
			if (typeof(G_vmlCanvasManager) != "undefined"){G_vmlCanvasManager.initElement(canvas);}
			var ctx = canvas.getContext("2d");

			drawHeadlinesRightPopOut(ctx);
		}
	}

	function drawHeadlinesRightPopOut(ctx) {
		// layer1/Path
		ctx.save();
		ctx.beginPath();
		ctx.moveTo(10.0, 0.0);
		ctx.lineTo(0.0, 15.0);
		ctx.lineTo(0.0, 0.0);
		ctx.lineTo(10.0, 0.0);
		ctx.closePath();
		ctx.fillStyle = '';
		ctx.fill();
		ctx.restore();
	}

