//show disclaimer when clicking on outside website, then open url in new window
	function showDisclaimer(url)
	{ message = 'DISCLAIMER\n\n You are now being redirected to an external website.\n\n ACISD accepts no responsibility for the content of this website.';
		if (confirm(message)) 
		{
			window.open(url);
			return true;
		}
		else return false;
	}
