//this will be executed after we "fetch" the contents of a spreadsheet. The json parameter will hold our spreadsheet's data

//https://michelleminkoff.com/2010/11/22/using-google-spreadsheets-as-your-database/

var videoCount =0;
function displayContent(json) {
    //figure out how many rows our spreadsheet has
    var len = json.feed.entry.length

    //console.log('length: '+len);

    //loop through the spreadsheet, gathering data
    for (var i=0; i<len; i++) {
        //for each row, add the following to actual HTML, grabbing it as a list, and then joining the list together as one long string.
        //Uses HTML for table cells, and then grabs attributes from the spreadsheet, using appropriate syntax. Enter your table header in the Google spreadsheet between            
        //the gsx$ and .$t.

            if (json.feed.entry[i].gsx$district.$t == "y")
            {   
                if(json.feed.entry[i].gsx$type.$t == "Blank" || json.feed.entry[i].gsx$type.$t == "blank")
                {
                    if (videoCount < 1)
                    {
                    $('#video').html('');
                    }
                    videoCount++;
                }
                
                else if(json.feed.entry[i].gsx$type.$t == 'Image' || json.feed.entry[i].gsx$type.$t == 'image')
                {
                    if (videoCount < 1)
                    {
                        var img ='';
                        var blurb ='';
                        
                        //check that there is a link
                        if (json.feed.entry[i].gsx$link.$t != '' && json.feed.entry[i].gsx$link.$t != 'na')
                        {
                            //if there is put it in
                            img='';
                        }
                        else{
                            //if there is not dont add a link
                            img='';
                        }
                        
                        //check that there is a blurb
                        if (json.feed.entry[i].gsx$blurb.$t != '' && json.feed.entry[i].gsx$blurb.$t != 'na')
                        {
                            //if there is put it in
                            blurb='

'+json.feed.entry[i].gsx$blurb.$t+'';
                        }
                        else{
                            blurb='';
                        }
                        
                        $('#video').html(img+blurb);
                        
                    }
                    videoCount++;
                }
                else if(json.feed.entry[i].gsx$type.$t == "Video" || json.feed.entry[i].gsx$type.$t == "video")
                 {
                    //https://www.youtube.com/embed/BUuAyRZi_lo?rel=0  you want BUuAyRZi_lo -->
                    if (videoCount < 1)
                    {
                        if(json.feed.entry[i].gsx$embed.$t)
                           {
                            $('#video').html('



'+json.feed.entry[i].gsx$blurb.$t+'');
                           }

                    }
                    videoCount++;

                    if(json.feed.entry[i].gsx$embed.$t)
                           {
                    
                    var html ='';
                    html += '
';
                        
                    html += '	
';
                    html += '		
';
                    html += '	';
                    html += '	'+json.feed.entry[i].gsx$title.$t+'
';
                    html += '	
'+json.feed.entry[i].gsx$blurb.$t+'';
                    html += '';

                    $('#video-library').append(html);
                           }
                }
            }

    }
    //put all three of our HTML strings into our div we made at the top of the page
    //document.getElementById('table-container').innerHTML = pre_html + actual_html + post_html

}

//https://webdesign.tutsplus.com/tutorials/how-to-lazy-load-embedded-youtube-videos--cms-26743
//there are styles in style.css

( function() {

var youtube = document.querySelectorAll( ".youtube" );

for (var i = 0; i < youtube.length; i++) {

    var source = "https://img.youtube.com/vi/"+ youtube[i].dataset.embed +"/hqdefault.jpg";

    var image = new Image();
            image.src = source;
            image.alt = youtube[i].dataset.alt;
            image.addEventListener( "load", function() {
                youtube[ i ].appendChild( image );
            }( i ) );

            youtube[i].addEventListener( "click", function() {

                var iframe = document.createElement( "iframe" );

                        iframe.setAttribute( "frameborder", "0" );
                        iframe.setAttribute( "allowfullscreen", "" );
                        iframe.setAttribute( "src", "https://www.youtube.com/embed/"+ this.dataset.embed +"?rel=0&showinfo=0&autoplay=1" );

                        this.innerHTML = "";
                        this.appendChild( iframe );
            } );    
};

} )();

function googleTranslateElementInit() {
  new google.translate.TranslateElement({pageLanguage: 'en', layout: google.translate.TranslateElement.InlineLayout.HORIZONTAL, gaTrack: true, gaId: 'UA-84500038-1'}, 'google_translate_element');
}

