        "ChannelBar": function() {
            $(".sw-channel-item").unbind('hover');
            $(".sw-channel-item").hover(function(){
                $(".sw-channel-item ul").stop(true, true);
                var subList = $(this).children('ul');
                if ($.trim(subList.html()) !== "") {
                    subList.slideDown(300, "swing");
                }
                $(this).addClass("hover");
            }, function(){
                $(".sw-channel-dropdown").slideUp(300, "swing");
                $(this).removeClass("hover");
            });

            $(".sw-channel-item").each(function(){
            	if($(".sw-channel-dropdown li", this).length == 0) {
                	$(this).addClass("no-dropdown");
                }
            });
        },

        "ChannelBarDropdown": function() {
        	$(".sw-channel-item").each(function(){
            	var sections = $(".sw-channel-dropdown >li",this).length;
                if(sections == "2"){
                	$("ul.sw-channel-dropdown",this).css("width","630px");
                } else if(sections == "1"){
                	$("ul.sw-channel-dropdown",this).css("width","335px");
                }

                var channelBarOuterSpace = "";
                var channelBarWidth = $("ul.sw-channel-list").width();
                if ($(window).width() > channelBarWidth){
                	channelBarOuterSpace = ($(window).width() - channelBarWidth)/2;
                } else {
                	channelBarOuterSpace = "0";
                }
                var windowWidth = $(window).width();
                var channelWidth = $(this).width();
                var channelDropdownWidth = $(".sw-channel-dropdown",this).width();
                var leftPos = $(this).position().left;
                //var rightPos = leftPos + channelWidth;
                var rightPos = leftPos + channelBarOuterSpace;

                var rightOffset = rightPos - channelDropdownWidth - 10;
                //var leftOffset = (channelBarWidth - leftPos) - channelDropdownWidth - 10;
                var leftOffset = (windowWidth - leftPos) - channelDropdownWidth - 10;

                //ADJUST DROPDOWN POSITION IF MORE THAN HALF WAY ACROSS THE CONTAINER
                var leftPercent = leftPos/channelBarWidth;
                if(leftPercent >= ".4") {
                	$("ul.sw-channel-dropdown",this).css({
                    	"left": "auto",
                        "right": "0px"

                    });
                    $(this).addClass("right-aligned");
                } else {
                	$(this).removeClass("right-aligned");
                }

                //ADJUST DROPDOWN POSITION IF OUTSIDE THE WINDOW
                if($(this).hasClass("right-aligned")){
                    if(channelDropdownWidth > rightPos){
                        $("ul.sw-channel-dropdown",this).css("right",rightOffset);
                    }
                //} else if(channelBarWidth - leftPos < channelDropdownWidth){
                } else if(windowWidth - leftPos < channelDropdownWidth){
                    $("ul.sw-channel-dropdown",this).css("left",leftOffset);
                } else {
                	$("ul.sw-channel-dropdown",this).css("left","0px");
                }
            });
        },

        "StickyChannelBar": function() {
        	if(!this.IsMyViewPage) {
                switch(this.GetBreakPoint()) {
                    case "desktop":
                        var channelBarOffSet = $("nav").offset().top;
                        if ($(window).scrollTop() <= channelBarOffSet) {
                            if(this.ChannelBarIsSticky) {
                                $("nav").removeClass("sticky").removeAttr("style");

                                this.ChannelBarIsSticky = false;
                            }

                            if($(".sp").length && $(".ui-widget.app.calendar").length) {
                                $(".wcm-controls").css("margin-top", "0px");
                            }
                        } else {
                            $("nav").css("height", $("#gb-channel-list-outer").outerHeight(true));

                            if(!this.ChannelBarIsSticky) {
                                $("nav").addClass("sticky");

                                this.ChannelBarIsSticky = true;
                            }

                            if($(".sp").length && $(".ui-widget.app.calendar").length) {
                            	if($(".wcm-controls").hasClass("wcm-stuck")) {
                                	$(".wcm-controls").css("margin-top", $("#gb-channel-list-outer").outerHeight(true));
                                }
                            }
                        }

                        $("#sw-maincontent").css({
                            "display": "block",
                            "position": "relative",
                            "top": "-" + $("#gb-channel-list-outer").outerHeight(true) + "px"
                        });
                    break;
                    case "768": case "640": case "480": case "320":
                        if(this.ChannelBarIsSticky) {
                            $("nav").removeClass("sticky").removeAttr("style");

                            this.ChannelBarIsSticky = false;
                        }

                        if($(".sp").length && $(".ui-widget.app.calendar").length) {
                            $(".wcm-controls").css("margin-top", "0px");
                        }
                        $("#sw-maincontent").removeAttr("style");
                    break;
                }
            }
        },

        "Body": function() {
        	// FOR SCOPE
            var _this = this;

            //HIDE REGIONS IF THERE ARE NO APPS IN THEM
            if($('.hp #sw-content-container1').is(':empty')){
            	$('#hp-content-row-one').css("padding","0px");
            }
            if($('.hp #sw-content-container2').is(':empty') && $('.hp #sw-content-container3').is(':empty')){
            	$('#hp-content-row-two').css("padding","0px");
            }
            if($('.hp #sw-content-container4').is(':empty')){
            	$('#hp-content-row-three').css("padding","0px");
            }

            // ADD CLASS TO NAV IF DASHBOARD IS ACTIVE
            if($("#dashboard-sidebar").length) {
                $("nav").addClass("dashboard-active");
            }

            // AUTO FOCUS SIGN IN FIELD
            $("#swsignin-txt-username").focus();

            // APPLY RESPONSIVE DIMENSIONS TO CONTENT IMAGES
            $(".ui-widget.app .ui-widget-detail img")
                .not($(".ui-widget.app.multimedia-gallery .ui-widget-detail img"))
                .each(function() {
                    if ($(this).attr('width') !== undefined && $(this).attr('height') !== undefined) { // IMAGE HAS INLINE DIMENSIONS
                        $(this).css({"display": "inline-block", "width": "100%", "max-width": $(this).attr("width") + "px", "height": "auto", "max-height": $(this).attr("height") + "px"});
                    }
            });

            // ADJUST FIRST BREADCRUMB
            $("li.ui-breadcrumb-first > a > span").text("Home");

            //REMOVE TEXT FROM RSS BUTTON
            $("a.ui-btn-toolbar.rss span").text("");

            //MOVE THE ARROW CONTROLS AND THE VIEW ALL TO THE HEADER
            $(".hp-column.two div.ui-widget.app, .hp-column.three div.ui-widget.app, .sp div.ui-widget.app").each(function(appIndex, app){
                if( $('.ui-btn-toolbar.rss', app).length > 0 ){
                    $('.ui-btn-toolbar.rss', app).appendTo( $('.ui-widget-detail', app) );
                }

                //$(".ui-widget-header a", app).wrapAll('');
                $(".ui-read-more", app).next("a.ui-btn-toolbar.rss", app).andSelf().wrapAll('');

            });

            // CHECK PAGELIST HEADER
            if($.trim($(".ui-widget.app.pagenavigation .ui-widget-header").text()) == "") {
            	$(".ui-widget.app.pagenavigation .ui-widget-header").html("
");
            }
        },

        "Shortcuts": function() {
        	this.BuildShortcuts({
            	"columns": [3, 1, 1, 1, 1],
                "selector": ".hp-column.one .ui-widget.app.siteshortcuts"
            });

            this.BuildShortcuts({
            	"columns": [2, 1, 1, 1, 1],
                "selector": ".hp-column.two .ui-widget.app.siteshortcuts"
            });

            this.BuildShortcuts({
            	"columns": [3, 1, 1, 1, 1],
                "selector": ".hp-column.four .ui-widget.app.siteshortcuts"
            });
        },

        "BuildShortcuts": function(props) {
        	var _this = this;

        	var columnNums = props.columns;
            var selector = props.selector;

            // RETURN BREAKPOINT INDEX
            var bp = function() {
                switch(_this.GetBreakPoint()) {
                    case "desktop": return 0; break;
                    case "768": return 1; break;
                    case "640": return 2; break;
                    case "480": return 3; break;
                    case "320": return 4; break;
                }
            }

            // SET COLUMN NUM AND OTHER VARS
            var columnNum = columnNums[bp()];
            var endRange;

            $(selector).each(function() {
            	// RETURN THE LI'S TO THE ORIGINAL UL
                $(".ui-widget-detail > ul.site-shortcuts", this).append($(".site-shortcuts-column > li", this));

                // REMOVE COLUMN CONTAINER FOR REBUILD
                $(".site-shortcuts-columns", this).remove();

                // GET SHORTCUT NUM
            	var shortcutNum = $(".ui-widget-detail > ul.site-shortcuts > li", this).length;

                // ADD COLUMN CONTAINER
                $(".ui-widget-detail", this).prepend('
');

                // LOOP TO BUILD COLUMNS
                for(var i = 0; i < columnNum; i++) {
                	// KEEP FROM ADDING EMPTY UL'S TO THE DOM
                    if(i < shortcutNum) {
                		// IF shortcutNum / columnNum REMAINDER IS BETWEEN .0 AND .5 AND THIS IS THE FIRST LOOP ITERATION
                    	// WE'LL ADD 1 TO THE END RANGE SO THAT THE EXTRA LINK GOES INTO THE FIRST COLUMN
                        if((shortcutNum / columnNum) % 1 > 0.0 && (shortcutNum / columnNum) % 1 < 0.5 && i == 0) {
                            endRange = Math.round(shortcutNum / columnNum) + 1;
                        } else if((shortcutNum / columnNum) % 1 == 0.5 && i >= (columnNum / i)) {
                        	endRange = Math.round(shortcutNum / columnNum) - 1;
                        } else {
                            endRange = Math.round(shortcutNum / columnNum)
                        }

                        // ADD THE COLUMN UL
                        $(".site-shortcuts-columns", this).append('');

                        // MOVE THE RANGE OF LI'S TO THE COLUMN UL
                        for(var r = 1; r <= endRange; r++) {
                                if($(".ui-widget-detail > ul.site-shortcuts > li:first-child", this).length) {
                                        $(".site-shortcuts-column.column-" + (i + 1), this).append($(".ui-widget-detail > ul.site-shortcuts > li:first-child", this));
                                }
                        }
                	}
                }

                // HIDE THE ORIGINAL UL
                $(".ui-widget-detail > ul.site-shortcuts", this).hide();
            });
        },

        "Footer": function() {

        	 // CHECK PHONE AND FAX
            if($.trim("540-658-6000") == "") {
            	$(".gb-footer-contact.phone").remove();
            }
            if($.trim("540-658-4065") == "") {
            	$(".gb-footer-contact.fax").remove();
            }

        	// ADD FOOTER LINKS
            var footerLinkStructure = "";
            var footerLinks = [
                ['Non Discrimination Policy','https://www.staffordschools.net/domain/3751','_blank'],
                ['Human Resources','https://www.staffordschools.net/domain/3003','_self'],
                ['Community Newsletter','https://www.staffordschools.net/site/default.aspx?PageID=18254','_self']
            ];

            for(var i = 0; i < 3; i++) {
            	if(footerLinks[i][0] != "" && footerLinks[i][1] != ""){
                	footerLinkStructure += '
' + footerLinks[i][0] + '';
                    $(".gb-footer-links ul").removeClass("hidden");
                }
            }
            $(".gb-footer-links ul").append(footerLinkStructure);

        	// FOR SCOPE
            var _this = this;

            // MOVE Bb FOOTER STUFF
            $(".gb-schoolwires-footer.logo").html($("#sw-footer-logo").html());
            var schoolwiresLinks = '';
            schoolwiresLinks += '
' + $.trim($("#sw-footer-links li:eq(0)").html().replace("|", "")) + '';
            schoolwiresLinks += '
' + $.trim($("#sw-footer-links li:eq(2)").html().replace("|", "")) + '';
            schoolwiresLinks += '
' + $.trim($("#sw-footer-links li:eq(1)").html().replace("|", "")) + '';
            $(".gb-schoolwires-footer.links").append('' + schoolwiresLinks + '');
            $(".gb-schoolwires-footer.copyright").append($("#sw-footer-copyright").html());

            // REMOVE FOOTER LINK IF NOT IN USE
            $('.gb-footer[data-show-email="false"] a.email').remove();
        },

        "Slideshow": function() {
        	// FOR SCOPE
            var _this = this;

            if($("#sw-content-container10.ui-hp .ui-widget.app.multimedia-gallery").length) {
                this.MMGPlugin();
            }

            // MMG PLACEHOLDER
            if($(".ui-hp").length && !$("#sw-content-container10.ui-hp .ui-widget.app.multimedia-gallery").length) {
                $.csMultimediaGalleryDefault({
                    "contentRegion" : "#sw-content-container10.ui-hp",
                    "defaultImageSrc" : "/cms/lib/VA01818723/Centricity/Template/20/defaults/ArchitectMain.png",
                    "allLoaded": function() {
                        _this.MMGPlugin();
                    }
                });
            }
        },

        "MMGPlugin": function() {
            // FOR SCOPE
            var _this = this;

            var mmg = eval("multimediaGallery" + $("#sw-content-container10.ui-hp .ui-widget.app.multimedia-gallery:first").attr("data-pmi"));
            mmg.props.defaultGallery = false;

            var mmgWidth = 1500;
            var transitionType = "fade";
            var fullScreenBreakpoints = [960];
            if("smaller" == "smaller;larger" || "smaller" == "smaller") {
                mmgWidth = 1220;
                transitionType = "slide";
                fullScreenBreakpoints = [960, 768, 640, 480, 320];
            }

            if($("#sw-content-container10.ui-hp .ui-widget.app.multimedia-gallery").length) {
                var mmg = eval("multimediaGallery" + $("#sw-content-container10.ui-hp .ui-widget.app.multimedia-gallery:first").attr("data-pmi"));
                mmg.props.defaultGallery = false;

                $("#sw-content-container10.ui-hp .ui-widget.app.multimedia-gallery:first").csMultimediaGallery({
                    "efficientLoad" : true,
                    "imageWidth" : mmgWidth,
                    "imageHeight" : 590,
                    "mobileDescriptionContainer": [768, 640, 480, 320], // [960, 768, 640, 480, 320]
                    "galleryOverlay" : false,
                    "linkedElement" : [],  // ["image", "title", "overlay"]
                    "playPauseControl" : true,
                    "backNextControls" : true,
                    "bullets" : false,
                    "thumbnails" : false,
                    "thumbnailViewerNum": [3, 4, 3, 3, 2], // NUMERICAL - [960 view, 768 view, 640 view, 480 view, 320 view]
                    "autoRotate" : true,
                    "hoverPause" : true,
                    "transitionType" : transitionType,
                    "transitionSpeed" : 2,
                    "transitionDelay" : 6,
                    "fullScreenRotator" : false,
                    "fullScreenBreakpoints" : fullScreenBreakpoints, // NUMERICAL - [960, 768, 640, 480, 320]
                    "onImageLoad" : function(props) {
                    	//WHOLE IMAGE CLICKABLE
                        if($(".mmg-slide.active .mmg-description-link.read-more", props.element).length && "true" == "true" ) {
                            var getPhotoLink = $(".mmg-slide.active .mmg-description-link.read-more", props.element).attr("href");
                            if(!$(".mmg-slide.active .mmg-image > a", props.element).length) {
                                $(".mmg-slides .mmg-slide.active img", props.element).wrap("");
                            }
                        }
                    }, // props.element, props.recordIndex, props.mmgRecords
                    "allImagesLoaded" : function(props) {}, // props.element, props.mmgRecords
                    "onTransitionStart" : function(props) {}, // props.element, props.currentRecordIndex, props.currentGalleryIndex, props.nextRecordIndex, props.nextGalleryIndex, props.mmgRecords
                    "onTransitionEnd" : function(props) {
                    	//WHOLE IMAGE CLICKABLE
                        if($(".mmg-slide.active .mmg-description-link.read-more", props.element).length && "true" == "true" ) {
                            var getPhotoLink = $(".mmg-slide.active .mmg-description-link.read-more", props.element).attr("href");
                            if(!$(".mmg-slide.active .mmg-image > a", props.element).length) {
                                $(".mmg-slides .mmg-slide.active img", props.element).wrap("");
                            }
                        }
                    }, // props.element, props.currentRecordIndex, props.currentGalleryIndex, props.mmgRecords
                    "allLoaded" : function(props) {
                        $("#hp-slideshow .mmg-container .mmg-control").wrapAll('
');
                        $("#hp-slideshow .mmg-container .mmg-control.play-pause").insertAfter("#hp-slideshow .mmg-container .mmg-control.back");

                        var windowWidth = $(window).width();
                        var imageWidth = $("#sw-content-container10").width();
                        var overlayOffset = (windowWidth-imageWidth)/2;
                        if($("#dashboard-sidebar").length) {
                        	overlayOffset = overlayOffset - 25;
                        }
                        $(".hp-slideshow-overlay").css("width",overlayOffset);
                    }, // props.element, props.mmgRecords
                    "onWindowResize": function(props) {
                        var windowWidth = $(window).width();
                        var imageWidth = $("#sw-content-container10").width();
                        var overlayOffset = (windowWidth-imageWidth)/2;
                        if($("#dashboard-sidebar").length) {
                        	overlayOffset = overlayOffset - 25;
                        }
                        $(".hp-slideshow-overlay").css("width",overlayOffset);
                    } // props.element, props.mmgRecords
                });
            }
        },

        "ModEvents": function() {
            $(".ui-widget.app.upcomingevents").modEvents({
				columns     : "yes",
				monthLong   : "yes",
				dayWeek     : "yes"
			});

			$(".ui-widget.app.upcomingevents .ui-article").each(function() {
				if (!$("h1.ui-article-title.sw-calendar-block-date", this).length && $.trim($(".ui-article-description", this).text()) != "There are no upcoming events to display."){
					var moveArticle = $(this).html();
					$(this).parent().prev("li").find(".upcoming-column.right").append(moveArticle);
					$(this).parent().remove();
				}
                var countLi = 0;
                if($('.upcoming-column.left h1', this).length){

                    var newDateTime = $('.upcoming-column.left h1', this).html().split("");
                    var newDate = '';
                    var dateIndex = ((newDateTime.length > 2) ? 2 : 1); //if the dayWeek is set to yes we need to account for that in our array indexing

                    //add a zero to the date if it is a single digit
                    if( newDateTime[dateIndex].length == 1 ) newDateTime[dateIndex] = "0" + newDateTime[dateIndex];



                    //if we have the day of the week make sure to include it.
                    if( dateIndex > 1 ){ 
                        newDate += newDateTime[0] + "";
                    }

                    //add in the month
                    newDate += newDateTime[dateIndex - 1] + ''; //the month is always the in the left array position to the date

                     //wrap the date in a new tag
                    newDate += '' + newDateTime[dateIndex] + '';

                    //append the date and month back into their columns
                    $('.upcoming-column.left h1', this).html(newDate);	

                    //add an ALL DAY label if no time was given
                    $('.upcoming-column.right .ui-article-description', this).each(function(){
                        if( $('.sw-calendar-block-time', this).length < 1 ){ //if it doesnt exist add it
                            $(this).prepend('ALL DAY');
                        }
                        //$('.sw-calendar-block-title', this).insertBefore($('.sw-calendar-block-time', this));
                    });

                    var list = $('.upcoming-column.left h1',this);
                    var listItems = list.children('span[class*="joel"]');
                    var mattDate = list.children('span[class*="matt-date"]')
                    list.prepend(listItems.get().reverse());
                    list.prepend(mattDate);

                    $('.upcoming-column.left h1 span[class*="joel"]',this).wrapAll('
');

                } 
			});

            $('.upcoming-column.right .ui-article-description').each(function(){
                  $('.sw-calendar-block-title', this).insertBefore($('.sw-calendar-block-time', this));
              });


            $("#hp-content-row-one .ui-widget.app.upcomingevents .ui-article, #hp-content-row-three .ui-widget.app.upcomingevents .ui-article").each(function() {
            	if($('.upcoming-column.right', this).length){
                	var month = $(".upcoming-column.left .joel-month",this).text();
                    var day = $(".upcoming-column.left .matt-date",this).text();
                	var eventNums = $(".ui-article-description",this).length;
                    if(eventNums > 2){
                    	var moreLink = 'MORE';
                        $('.upcoming-column.right', this).append(moreLink);
                        $(".ui-article-description:nth-child(n + 3)",this).hide();

                    }
                }


                $(".cs-ue-more-link",this).on("click", function(e) {
                	if($(this).hasClass("open")){
                        e.preventDefault();
                        $(this).removeClass("open").attr('aria-label','show more events for ' + month + ' ' + day);
                        $(this).parent().find(".ui-article-description:nth-child(n + 3)").slideUp();
                        $(".cs-ue-more-link-text",this).text("MORE");
                    } else {
                    	e.preventDefault();
                        $(this).addClass("open").attr('aria-label','show less events for ' + month + ' ' + day);
                        $(this).parent().find(".ui-article-description:nth-child(n + 3)").slideDown();
                        $(".cs-ue-more-link-text",this).text("LESS");
                    }
                });
            });

        },

