		var video = document.getElementById('video_336414').value;
		if(speed == '' || speed == null){
			speed = 800;
		}
		
		var direction = directions_336414(slideshowMotion);
		var directionOn = direction.directionOn;
		var directionOff = direction.directionOff;
		
