				$('#videoShell_336414' + nums).fadeOut(speed);
			}
			if($('#dipContent_336414' + nums).length){
				$('#dipContent_336414' + nums).fadeOut(speed);
			}
			
			if(nums >= totalDips){nums = 1;}else{nums = nums + 1;}
			
			if($('#nav_336414' + nums).length){
				$('#nav_336414' + nums).addClass('on');
