	var CreativeTemplate = {
    	// PROPERTIES
        "KeyCodes": { "tab": 9, "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40 },
        "PageTypes": { "1": "pagetype-directory", "6": "pagetype-searchresults", "7": "pagetype-signin", "8": "pagetype-signedout", "9": "pagetype-passwordreset", "10": "pagetype-register", "15": "pagetype-sitemap", "19": "pagetype-404" },
        "IsMyViewPage": false, // UPDATES IN SetTemplateProps METHOD
        "ShowDistrictHome": true,
        "ShowSchoolList": false, // UPDATES IN SetTemplateProps METHOD
        "ShowTranslate": true,
		"ShowFullSiteButton": true,
        "ChannelBarIsSticky": false,
        "SlideshowDescFixed": false,

		// METHODS
        "Init": function() {
            // FOR SCOPE
            var _this = this;

            this.SetTemplateProps();
            this.JsMediaQueries();
			this.MyStart();
			this.SchoolList();
			this.Translate();
            this.Header();
            this.Search();
            this.ChannelBar();
            this.StickyChannelBar();
            this.Slideshow();
            this.CheckSlideshow();
            this.StreamingVideo();
            this.Body();
            this.Shortcuts();
            this.TabbedBlocks();
            this.ModEvents();
            this.GlobalIcons();
            this.RsMenu();
            this.AppAccordion();
            this.Footer();
            this.SocialIcons();
            this.ViewFullSite();

            $(window).load(function() { _this.WindowLoad(); });
            $(window).resize(function() { _this.WindowResize(); });
            $(window).scroll(function() { _this.WindowScroll(); });

			csGlobalJs.OpenInNewWindowWarning();
        },

        "SetTemplateProps": function() {
        	// MYVIEW PAGE CHECK
            if($("#pw-body").length) this.IsMyViewPage = true;

            // SCHOOL LIST CHECK
        	if($(".sw-mystart-dropdown.schoollist").length) this.ShowSchoolList = true;
        },

        "WindowLoad": function() {
			this.ChannelBar();
            csGlobalJs.OpenInNewWindowWarning();
        },

        "WindowResize": function() {
            this.JsMediaQueries();
            this.StickyChannelBar();
            this.CheckSlideshow();
            this.Shortcuts();

            // ADJUST SEARCH
            var iconToggle = ($(".gb-mystart.right .gb-global-icons-toggle").length) ? $(".gb-mystart.right .gb-global-icons-toggle").outerWidth(true) : 0;
            if($("#gb-search").attr("aria-hidden") == "false") {
                $("#gb-search").css({
                	"right": iconToggle,
                    "width": ($("#gb-mystart").width() - iconToggle)
                });
            }
        },

        "WindowScroll": function() {
            this.StickyChannelBar();
            this.CheckSlideshow();
        },

        "JsMediaQueries": function() {
            switch(this.GetBreakPoint()) {
                case "desktop": 

                break;
                case "768":

                break;
                case "640":
					this.CheckTabbedAccordion();
                break;
                case "480":
					this.CheckTabbedAccordion();
                break;
                case "320":
					this.CheckTabbedAccordion();
                break;
            }
        },

        "MyStart": function() {
        	// FOR SCOPE
            var _this = this;

			// ADD DISTRICT HOME
            if(this.ShowDistrictHome && $(".sw-mystart-button.home").length) {
            	$(".sw-mystart-button.home > a > span").text("District Home");
            	$(".gb-mystart.left").prepend($(".sw-mystart-button.home"));
            }

            // BUILD USER OPTIONS DROPDOWN
            var userOptionsItems = "";



			// SIGNIN BUTTON
            if($(".sw-mystart-button.signin").length) {
				userOptionsItems += "
" + $(".sw-mystart-button.signin").html() + "";
			}

			// REGISTER BUTTON
            if($(".sw-mystart-button.register").length) {
                userOptionsItems += "
" + $(".sw-mystart-button.register").html() + "";
            }



			// ADD USER OPTIONS DROPDOWN TO THE DOM
            $(".cs-mystart-dropdown.user-options .cs-dropdown-list").html(userOptionsItems);

			// BIND DROPDOWN EVENTS
			this.DropdownActions({
            	"dropdownParent": ".cs-mystart-dropdown.user-options",
                "dropdownSelector": ".cs-selector",
                "dropdown": ".cs-dropdown",
                "dropdownList": ".cs-dropdown-list"
            });
        },

		"SchoolList": function() {
			// ADD SCHOOL LIST
			if(this.ShowSchoolList) {
				var schoolDropdown =	'
' +
											'
GPS School Directory' +
											'
' +
												'' + $(".sw-mystart-dropdown.schoollist .sw-dropdown-list").html() + '' +
											'' +
										'';

				// ADD SCHOOL LIST TO THE DOM	
				$(".gb-mystart.left").append(schoolDropdown);

				// BIND DROPDOWN EVENTS
				this.DropdownActions({
					"dropdownParent": ".cs-mystart-dropdown.schools",
					"dropdownSelector": ".cs-selector",
					"dropdown": ".cs-dropdown",
					"dropdownList": ".cs-dropdown-list"
				});
			}
		},

