	$(window).load(function(){
		searchText();
		calendarListView();
        clearLogo();
        pgScreenSize();
        adjustGlobalLinkLabels();

       if( $('.hp').length > 0 ){
            var maxH = 0;
            $('.hp.row.three .column').css('min-height', 0);
            $('.hp.row.three .column').each(function(e){
                if( $(this).outerHeight() > maxH ) maxH = $(this).outerHeight();
            });
            $('.hp.row.three .column').css('min-height', maxH);

            var v1 = $('#hp-headline-scroller');
            if( $('.headlines-scroll-ctrl', v1).length > 0 ){
                $('.headlines-scroll-ctrl', v1).css('top', (($('.ui-widget-detail .ui-article-outer', v1).outerHeight()/2) + $('.ui-widget-header', v1).outerHeight() + 30) );
            }
        }
	});

