        $(window).scroll(function(e){
            if( $(window).scrollTop() > 0){
            	if( !$('#gb-page').hasClass('sticky-channel') ) $('#gb-page').addClass('sticky-channel');

            }else if($('#gb-page').hasClass('sticky-channel')){
            	$('#gb-page').removeClass('sticky-channel');
            }
        });
	});	

	function pgScreenSize(){
		var getWidth = $(window).width();

        if( $('html.ff').length > 0 ) getWidth = window.innerWidth; //added by Jeremy - case #00351633 to fix FF window width calculation

		multimediaRotator(); 
        clearLogo();
		if (getWidth >= 1024) { //Desktop View
            if( $('.hp').length > 0 ){
            	var maxH = 0;
                $('.hp.row.three .column').css('min-height', 0);
            	$('.hp.row.three .column').each(function(e){
					if( $(this).outerHeight() > maxH ) maxH = $(this).outerHeight();
                });
                $('.hp.row.three .column').css('min-height', maxH);

                if($('#hp-scrolling-headlines').hasClass('moved-for-mobile')){
                	$('#hp-scrolling-headlines')
                    	.removeClass('moved-for-mobile')
                    	.prependTo('#hp-slideshow-outer');
                }
            }
            $('#sp-content-outer, #spn-content-outer').css('padding-top', $('header').outerHeight());
		}else{ //Mobile BPs
        	$('#sp-content-outer, #spn-content-outer').css('padding-top', 0);

            if(!$('#hp-scrolling-headlines').hasClass('moved-for-mobile')){
                $('#hp-scrolling-headlines')
                    .addClass('moved-for-mobile')
                    .prependTo('#hp-content-outer');
            }

        	if( getWidth < 768 ){
            	appView();
            }
		}

        if( $('.hp').length > 0 ){
            var v1 = $('#hp-headline-scroller');
            if( $('.headlines-scroll-ctrl', v1).length > 0 ){
                $('.headlines-scroll-ctrl', v1).css('top', (($('.ui-widget-detail .ui-article-outer', v1).outerHeight()/2) + $('.ui-widget-header', v1).outerHeight() + 25) );
            }
        }

        $('#hp-headline-scroller .app.headlines .ui-articles > li .ui-article').css('height', $('#hp-headline-scroller .app.headlines .ui-articles > li .ui-article').width());
	}

