                    "imageHeight" : 353,
                    "mobileDescriptionContainer": [640, 480, 320], // [960, 768, 640, 480, 320]
                    "galleryOverlay" : false,
                    "linkedElement" : [], // ["image", "title", "overlay"]
                    "playPauseControl" : true,
                    "backNextControls" : true,
                    "bullets" : false,
