        "PopulateSchoolList": function() {
        	var _this = this;


        	var elemSchools = "", middleSchools = "", k8 = "";
            var elemArray = [], middleArray = [], k8Array = [];
            $(".sw-mystart-dropdown.schoollist .sw-dropdown-list > li").each(function() {
                var schoolName = $.trim($("a", this).text()).toLowerCase();

                // CHECK FOR MIDDLE
                /*if(schoolName.indexOf("middle") > -1) {
                    middleSchools += '
' + $(this).html() + '';
                }

                // K8
                else if(schoolName.indexOf("traditional") > -1){
                    k8 += '
' + $(this).html() + '';
                }

                // CHECK FOR ELEMENTARY
                else {
                    elemSchools += '
' + $(this).html() + '';
                }

                $(".gb-school-panel.elementary").html('' + elemSchools + '');
                $(".gb-school-panel.middle").html('' + middleSchools + '');
                $(".gb-school-panel.k-8").html('' + k8 + '');*/

                //var panelNum = Math.ceil($(".ui-articles > li", this).length / props.RecordsPerPanel);

                // CHECK FOR MIDDLE
                if(schoolName.indexOf("middle") > -1) {
                    middleArray.push({ "name": $(this).text(), "url": $("> a", this).attr("href") });
                }

                // K8
                else if(schoolName.indexOf("traditional") > -1){
                    k8Array.push({ "name": $(this).text(), "url": $("> a", this).attr("href") });
                }

                // CHECK FOR ELEMENTARY
                else {
                    elemArray.push({ "name": $(this).text(), "url": $("> a", this).attr("href") });
                }


            });

            // ELEMENTARY
            var halfElem = Math.ceil(elemArray.length / 2);    
			var firstElemHalf = elemArray.splice(0, halfElem);
            var secondElemHalf = elemArray;
            var elemListOne = "", elemListTwo = "";
            $.each(firstElemHalf, function(index, school) {
            	elemListOne += '
' + school.name + '';
            });
            $.each(secondElemHalf, function(index, school) {
            	elemListTwo += '
' + school.name + '';
            });
            $(".gb-school-panel.elementary").html('' + elemListOne + '' + elemListTwo + '');

            // MIDDLE
            var halfMiddle = Math.ceil(middleArray.length / 2);    
			var firstMiddleHalf = middleArray.splice(0, halfMiddle);
            var secondMiddleHalf = middleArray;
            var middleListOne = "", middleListTwo = "";
            $.each(firstMiddleHalf, function(index, school) {
            	middleListOne += '
' + school.name + '';
            });
            $.each(secondMiddleHalf, function(index, school) {
            	middleListTwo += '
' + school.name + '';
            });
            $(".gb-school-panel.middle").html('' + middleListOne + '' + middleListTwo + '');

            // K8
            var halfK8 = Math.ceil(k8Array.length / 2);    
			var firstK8Half = k8Array.splice(0, halfK8);
            var secondK8Half = k8Array;
            var k8ListOne = "", k8ListTwo = "";
            $.each(firstK8Half, function(index, school) {
            	k8ListOne += '
' + school.name + '';
            });
            $.each(secondK8Half, function(index, school) {
            	k8ListTwo += '
' + school.name + '';
            });
            $(".gb-school-panel.k-8").html('' + k8ListOne + '' + k8ListTwo + '');
        },

        "ToggleSchools": function() {
            var _this = this;
        	$('.school-btn').bind('click keydown', function(e) {
            		if(_this.AllyClick(e)) {
                  		e.preventDefault();

                    if($(this).attr('aria-expanded') == "false") {
                     	$('#gb-school-list').stop().slideDown(200);
                        $(this).attr('aria-expanded',true);
                        $(this).addClass('rotate');
                        $('#gb-school-list li:eq(0) a').focus();
                        $('#gb-school-list a').attr('tabindex', 0);



                     } 

                     else {
                     	$('#gb-school-list').stop().slideUp(200);
                        $(this).attr('aria-expanded',false);
                        $(this).removeClass('rotate');
                         $('#gb-school-list a').attr('tabindex', -1);
                     }

                  }

            })

            $("#gb-school-list").focusout(function() {
            	setTimeout(function() {
                	if(!$("#gb-school-list").find(":focus").length) {
                    	_this.CloseSchoolMenu();
                		_this.TabInit();
                    }
                }, 500);
            });
        },

        "TabInit": function() {
        	// INITIALIZATION
            	$('.tab-button').removeClass('active');
                $(".tabs-container li:eq(0) .tab-button").addClass("active").attr("aria-selected", "true").attr("tabindex", "0");
                $(".tabs-container li:gt(0) .tab-button").attr("aria-selected", "false").attr("tabindex", "-1");
                $(".gb-school-panel:eq(0)").attr("aria-hidden", "false");
                $('.gb-school-panel:gt(0)').hide();


       },

        "CloseSchoolMenu":function() {
			$('#gb-school-list').stop().slideUp('fast');
            $('.school-btn').attr('aria-expanded',false);
            $('.school-btn').removeClass('rotate');
		},



        "HandleTabEvents": function() {

        	$('#gb-school-list').on('mouseleave', function() {
            	_this.CloseSchoolMenu();
                _this.TabInit();
            });

        	$('#gb-school-tab-list a').click(function(e){
            	e.preventDefault();
                $('#gb-school-tab-list a').removeClass('active')
                $(this).addClass('active');
            });

        	$('#gb-school-tab-list a').focus(function(e){
            	var item = $(this).parent().index();
                $('.gb-school-panel').hide().attr("aria-hidden", "true");
                $('.gb-school-panel').eq(item).show().attr("aria-hidden", "false");

            });

        	var _this = this;
            $('#gb-school-tab-list a').keydown(function(e){
                var csKeyCodes = { "tab": 9, "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40 };

                switch(e.keyCode) {

                	// CONSUME LEFT AND UP ARROWS
                    case csKeyCodes.left:
                    case csKeyCodes.up:
                        e.preventDefault();

                    if($(this).parent().index() == 0) {
                    	$('#gb-school-tab-list a').removeClass('active').attr("aria-selected", "false").attr("tabindex", "-1");
                    	$(this).closest('ul').find("li:last-child > a").focus().addClass('active');

                    }
                    else {
                    $('#gb-school-tab-list a').removeClass('active').attr("aria-selected", "false").attr("tabindex", "-1");
                    	$(this).parent().prev("li").find("a").focus().addClass('active');
                    }

                    break;

                    // CONSUME RIGHT AND DOWN ARROWS
                    case csKeyCodes.right:
                    case csKeyCodes.down:
                        e.preventDefault();

                        if($(this).parent().is(":last-child")) {
                        	$('#gb-school-tab-list a').removeClass('active').attr("aria-selected", "false").attr("tabindex", "-1");
                        	$(this).closest('ul').find("li:first-child > a").focus().addClass('active');
                        } else {
                        	$('#gb-school-tab-list a').removeClass('active').attr("aria-selected", "false").attr("tabindex", "-1");
                            $(this).parent().next().find("> a").attr("tabindex", "0").focus().addClass('active');
                        }

                    break;

                    // CONSUME ESC KEY
                    case csKeyCodes.esc:
                     e.preventDefault();
                    	$('.school-btn').focus();
                        _this.CloseSchoolMenu();
                        _this.TabInit();

                    break;


            	}
                if(e.shiftKey && e.keyCode == 9) {
                    e.preventDefault();
                    $('.school-btn').focus();
                        _this.CloseSchoolMenu();
                        _this.TabInit(); 

            	}

            });	
        },

        "HandlePanelEvents": function() {
        	var _this = this;
            /*
            $('#gb-school-panel-list a').keydown(function(e){
            	var csKeyCodes = { "tab": 9, "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40 };

            	switch(e.keyCode) {

                	// CONSUME LEFT AND UP ARROWS
