                        	$(this).closest('ul').find("li:first-child > a").focus();
                        } else {
                            $(this).parent().next().find("> a").attr("tabindex", "0").focus();
                        }

                    break;

                    // CONSUME ESC KEY
                    case csKeyCodes.esc:
                     e.preventDefault();
                    	$('.school-btn').focus();
                        _this.CloseSchoolMenu();

                    break;

                    // CONSUME TAB KEY
                    case csKeyCodes.tab:
                     e.preventDefault();

                        if($(this).is(":last")) {
                        	$('.school-btn ').focus();
                            _this.CloseSchoolMenu();
                            _this.TabInit();
                        } else {
                            $(this).parent().next().find("> a").attr("tabindex", "0").focus();
                        }

                    break;

                }

                if(e.shiftKey && e.keyCode == 9) {
                   e.preventDefault();
