                          if($(this).parent().is(":first-child")) {
                          	$('#gb-school-tab-list').find('.active').focus();
                          }

                      } else {
                          $(this).parent().prev().find("> a").attr("tabindex", "0").focus();
                      }

            	}


            });
            */
        	$(".gb-school-panel").each(function() {
            	var link = $("a", this);
                var firstLinkIndex = $(link).index($(link).first());
                var firstLink = $(link).eq($(link).index($(link).first()));
                var lastLinkIndex = $(link).index($(link).last());
                var lastLink = $(link).eq($(link).index($(link).last()));

                $(link).keydown(function(e) {
                    var increment;
                    var thisIndex = link.index(this);
                    var newIndex = -1;

                    switch(e.keyCode) {
                        case _this.KeyCodes.up:
                        case _this.KeyCodes.left:
                            e.preventDefault();

                            if($(this).is(firstLink)) {
                                newIndex = lastLinkIndex;
                            } else {
                                newIndex = thisIndex - 1;
                            }
                        break;

                        case _this.KeyCodes.down:
                        case _this.KeyCodes.right:
                            e.preventDefault();

                            if($(this).is(lastLink)) {
                                newIndex = firstLinkIndex;
                            } else {
                                newIndex = thisIndex + 1;
                            }
                        break;

                        case _this.KeyCodes.esc:
                            e.preventDefault();

                            _this.CloseSchoolMenu();
                            _this.TabInit();
                        break;
                    }

                    if(newIndex != -1) {
                        link.eq(newIndex).focus();
                    }
                });
            });
        },

        "Search": function() {
        	var _this = this;
            //SEARCH TEXT
            $("#gb-search-input").focus(function() {
            	setTimeout(function() {
                  if($("#gb-search-input").val() == "Search...") {
                      $("#gb-search-input").val("");
                  }
                }, 500);
            }).blur(function() {
                if($(this).val() == "") {
                    $(this).val("Search...");
                }
            });

            $('#gb-search-btn').on('click keydown', function(e) {
            	if(_this.AllyClick(e)) {
                  		e.preventDefault();

                        if($(this).attr('aria-expanded') == "false") {
                        	$(this).attr('aria-expanded',true).attr('disabled', true);
                        	$(this).addClass('changed');
             				$('.school-btn, .cs-selector, #gb-search-btn').attr('tabindex', -1).attr('aria-hidden', true);
                            $('#close').addClass('visible').attr('tabindex', 0).attr('aria-hidden', false);
                            $('#gb-search-input').addClass('open').attr('tabindex', 0);
                            $("#gb-search-input").val("");
                            $('#gb-search-submit').attr('tabindex', 0);

                            setTimeout(function() {
                            	$('#gb-search-input').focus();
                                $('#gb-search-submit').fadeIn('slow');
                            }, 100);
            			}
                    }
            });

            $('#close').on('click keydown', function(e) {
            	if(_this.AllyClick(e)) {
                    e.preventDefault();
                    $('#gb-search-submit').fadeOut('fast');
                    $('#gb-search-btn').removeClass('changed').attr('disabled', false);;
                    $('.sw-mystart-button a, .school-btn, .cs-selector, #gb-search-btn').attr('tabindex', 0).attr('aria-hidden', false);
                    $('#gb-search-btn').attr('aria-expanded',false);
                    $(this).removeClass('visible').attr('tabindex', -1).attr('aria-hidden', true);
                    $('#gb-search-input').removeClass('open').attr('tabindex', -1).val("Search...");
                    $('#gb-search-btn').focus();

                }
            });

            $("#gb-search-form").submit(function(e){
				e.preventDefault();
				if($("#gb-search-input").val() != "Search..." && $("#gb-search-input").val() != "") {
					window.location.href = "/site/Default.aspx?PageType=6&SiteID=4&SearchString=" + $("#gb-search-input").val();
                }
			});


        },


        "AllyClick": function(event) {
            if(event.type == "click") {
                return true;
            } else if(event.type == "keydown" && (event.keyCode == this.KeyCodes.space || event.keyCode == this.KeyCodes.enter)) {
                return true;
            } else {
                return false;
            }
        },



        "ModEvents":function() {
        	if (!$(".upcomingevents .joel-month").length) {
                $(".upcomingevents").modEvents({
                    columns     : "yes",
                    monthLong   : "no",
                    dayWeek     : "no"
                });
                this.EventsByDay(".upcomingevents .ui-articles");
            }
        },

        "EventsByDay":function(container) {
        	var countLi = 0;
		$(".ui-article", container).each(function(){
			if (!$(this).find("h1.ui-article-title.sw-calendar-block-date").size()){
				var moveArticle = $(this).html();
				$(this).parent().prev().children().children().next().append(moveArticle);
				$(this).parent().remove();
			};
		});
		$(".ui-article", container).each(function(){
			var newDateTime = $('.upcoming-column.left h1', this).html().split("");
			var newDate = '';
			var dateIndex = ((newDateTime.length > 2) ? 2 : 1); //if the dayWeek is set to yes we need to account for that in our array indexing


			//add in the month
			newDate += newDateTime[dateIndex - 1] + ''; //the month is always the in the left array position to the date

			//wrap the date in a new tag
			newDate += '' + newDateTime[dateIndex] + '';

			//append the date and month back into their columns
			$('.upcoming-column.left h1', this).html(newDate);	



             //WRAP DATE AND MONTH IN A CONTAINER
               $(".joel-day, .joel-month", this).wrapAll("");



		});




        $('.upcomingevents').find(".ui-articles li").each(function(){
                  var eventDate = $(this).find(".adam-hug").html();
                  var eventMonth = $(this).find(".joel-month").text();
                  var eventNumberDay = $(this).find(".jeremy-date").text();
                  var eventDay = $(this).find(".joel-day").html();
                  //var eventDetails = $(this).find(".upcoming-column.right").html();
                  var ariaMonth = "";
