                    break;
                    case "oct":
                      ariaMonth = "October";
                    break;
                    case "nov":
                      ariaMonth = "November";
                    break;
                    case "dec":
                      ariaMonth = "December";
                    break;
                    default: ariaMonth = eventMonth;
                  }
                  switch(eventNumberDay){
                    case "1":
                      ariaDate = "First";
                    break;
                    case "2":
                      ariaDate = "Second";
                    break;
                    case "3":
                      ariaDate = "Third";
                    break;
