                        }
                    });
                });


        },


        "AppScroller": function(){
        	if($(".hp").length) {

            	this.HeadlineCards();
            	var pass = 1;

                $('#headlines-scroller .ui-articles').addClass('old-list').after('');
            	$('#headlines-scroller .ui-articles > li').each(function(liIndex, li){
                    if(pass){
                    	$('#headlines-scroller .ui-articles.new-list').append('
' + $(li).html() + '');
                        pass = 0;
                    }else{
                    	$('#headlines-scroller .ui-articles.new-list li').last().append($(li).html());
                    	pass = 1;
                    }
				});

                $('#headlines-scroller .ui-articles.old-list').remove();

                $("#headlines-scroller .region .app.headlines").appScroller({
                        "appParent"             : "#headlines-scroller",
                        "targetApp"             : "headlines", //upcomingevents or headlines
                        "imageNum960"           : 3, //NUMBER OF IMAGES TO DISPLAY AT 960
                        "imageNum768"           : 2, //NUMBER OF IMAGES TO DISPLAY AT 768
                        "imageNum640"           : 1, //NUMBER OF IMAGES TO DISPLAY AT 640
                        "imageNum480"           : 1, //NUMBER OF IMAGES TO DISPLAY AT 480
                        "imageNum320"           : 1, //NUMBER OF IMAGES TO DISPLAY AT 320
                        "backIconFontLetter"    : "", //LETTER OR NUMBER TO USE FOR THE ICON FONT FOR THE BACK BUTTON
                        "nextIconFontLetter"    : "", //LETTER OR NUMBER TO USE FOR THE ICON FONT FOR THE BACK BUTTON
                        "buttonColor"           : "#FFF", //COLOR TO BE USED FOR THE BACK AND NEXT BUTTONS
                        "extraBackIconClass"    : "dev-icon-chevron-thin rotating-icon point-left",
                        "extraNextIconClass"    : "dev-icon-chevron-thin",
                        "controlGutter"         : "38" //LEFT and RIGHT MARGINS TO ALLOW ROOM FOR CONTROLS
                });

                $('#headlines-scroller .headlines .ui-articles > li .ui-article')
                	.attr('role', 'button')
                    .attr('tabindex', -1)
                    .attr('aria-label', 'Toggle this headline card');

                $('#headlines-scroller .app').each(function(appIndex, app){
                    if( $('.headlines-scroll-ctrl.back', app).length > 0 ){
                        $('.ui-widget-detail', app).before($('.headlines-scroll-ctrl.back', app));
                    }
                });

                $('#headlines-scroller .headlines .ui-articles > li.active .ui-article').attr('tabindex', 0);

                $('#headlines-scroller .headlines .ui-articles > li .ui-article').on('keydown', function(e) { // Trigger the click event from the keyboard
                    var code = e.which;
                    // 13 = Return, 32 = Space
                    if ((code === 13) || (code === 32)) {
                    	if(code === 32) e.preventDefault();
                        CreativeTemplate.ToggleHeadlineCards($(this));
                    }
                })
                .click(function(e){
                    CreativeTemplate.ToggleHeadlineCards($(this));
                })
                .hover(function(e){ //in
                        CreativeTemplate.ToggleHeadlineCards($(this));
                    },
                    function(e){ //out
                        CreativeTemplate.ToggleHeadlineCards($(this));
                });

                $('#headlines-scroller .headlines-scroll-ctrl').click(function(e){
                	$('#headlines-scroller .headlines .ui-articles > li .ui-article').attr('tabindex', -1);

                	$('#headlines-scroller .headlines .ui-articles > li.active .ui-article').attr('tabindex', 0);
                });
            }
        },

        "MultimediaSlider": function() {
        	var _this = this;

            // HEADLINES AND FEATURES
           	var recordsPerPanel = 0;
            switch(_this.GetBreakPoint()) {
                case "desktop":
                    recordsPerPanel = 6;
                break;

                case "768":
                case "640":
                    recordsPerPanel = 4;
                break;

                case "480":
                case "320":
                    recordsPerPanel = 2;
                break;
            }

            this.MakePanels({
            	"Selector": "#sw-content-container1.ui-hp .ui-widget.app.headlines, #sw-content-container6.ui-hp .ui-widget.app.headlines",
                "RecordsPerPanel": recordsPerPanel
            });

            // HEADLINES AND FEATURES ARTICLE SLIDER
            this.RunArticleSlider({
            	"Selector": "#sw-content-container1.ui-hp .ui-widget.app.headlines, #sw-content-container6.ui-hp .ui-widget.app.headlines"
            });

            // MMG
        	var mmg = "";
            var mmgRecords = "";

        	$("#sw-content-container1.ui-hp .ui-widget.app.multimedia-gallery, #sw-content-container6.ui-hp .ui-widget.app.multimedia-gallery, .sp .ui-widget.app.multimedia-gallery").each(function() {
            	// INTERCEPT THE MMG APP AND MARK IT AS NOT A DEFAULT GALLERY
                mmg = eval("multimediaGallery" + $(this).attr("data-pmi"));
            	mmg.props.defaultGallery = false;

                // SET PMI VARIABLE
                var PMI = $(this).attr("data-pmi");

                // REMOVE LAST EMPTY RECORD OBJECT TO KEEP THINGS TIDY
            	mmg.records.splice(-1, 1);

                // START THE ARTICLE STRUCTURE
                mmgRecords = '';

                // REMOVE MMG-CONTAINER TO KEEP THINGS CLEAN
                $(".mmg-container", this).remove();

                // LOOP THROUGH AND BUILD THE ARTICLES
                $.each(mmg.records, function(index, record) {
                	mmgRecords +=	'
' +
                    					'
' +
                                       		'
' +
                                            	'' +
                                                ((record.hideTitle == "False") ? '
' + record.title + ' ' : '') +
                                                ((record.videoIsEmbedded != "False") ? '' : '') +
                                            '' +
                                            '
' +
                                            	((record.hideCaption == "False") ? '
' + record.caption + '' : '') +
                                                ((record.isLinked != "False") ? '
' + record.linkText + '' : '') +
                                            '' +
                                        '' +
                    				'';

                });

                // END THE ARTICLE STRUCTURE
                mmgRecords += '';

                // ADD THE ARTICLES TO THE DOM
                $(".ui-widget-detail", this).append(mmgRecords);


                // BIND EVENTS
                $("#pmi-" + PMI).on("click keydown", ".article-video-button", function(e) {
                    if(_this.AllyClick(e)) {
                        e.preventDefault();

                        // GET ACTIVE RECORD INDEX
                        var recordIndex = $(this).attr("data-record-index");

                        function decodeIframeHtml(iframe) {
                            var textArea = document.createElement("textarea");
                            textArea.innerHTML = iframe;

                            return textArea.value;
                        }

                        function addUrlParameters(iframe) {
                            var addedParameters = "";

                            // YOUTUBE EMBED
                            if(iframe.indexOf("youtube.com/embed") > -1) {
                                if(iframe.indexOf("rel=") == -1) {
                                    addedParameters += "&rel=0";
                                }
                                if(iframe.indexOf("autoplay=") == -1) {
                                    addedParameters += "&autoplay=1";
                                }

                                if(iframe.indexOf("?") == -1) {
                                    // REMOVE THE FIRST "&"
                                    addedParameters = addedParameters.substr(1);

                                    // ADD PARAMETERS
                                    // MATCH: youtube.com/embed/(any character except space)(end match on ") - Replace: ($& is the matched value)(add ?)(add the built parameters)
                                    iframe = iframe.replace(/youtube.com\/embed\/[^\s]+?[^"]+/ig, '$&?' + addedParameters);
                                } else {
                                    // ADD PARAMETERS
                                    // MATCH: youtube.com/embed/(any character except space)(end match on ") - Replace: ($& is the matched value)(add the built parameters)
                                    iframe = iframe.replace(/youtube.com\/embed\/[^\s]+?[^"]+/ig, '$&' + addedParameters);
                                }
                            }

                            // VIMEO EMBED
                            if(iframe.indexOf("vimeo.com/video/") > -1) {
                                if(iframe.indexOf("autoplay=") == -1) {
                                    addedParameters += "&autoplay=1";
                                }

                                if(iframe.indexOf("?") == -1) {
                                    // REMOVE THE FIRST "&"
                                    addedParameters = addedParameters.substr(1);

                                    // ADD PARAMETERS
                                    // MATCH: youtube.com/embed/(any character except space)(end match on ") - Replace: ($& is the matched value)(add ?)(add the built parameters)
                                    iframe = iframe.replace(/vimeo.com\/video\/[^\s]+?[^"]+/ig, '$&?' + addedParameters);
                                } else {
                                    // ADD PARAMETERS
                                    // MATCH: youtube.com/embed/(any character except space)(end match on ") - Replace: ($& is the matched value)(add the built parameters)
                                    iframe = iframe.replace(/vimeo.com\/video\/[^\s]+?[^"]+/ig, '$&' + addedParameters);
                                }
                            }

                            // TEACHERTUBE EMBED
                            if(iframe.indexOf("teachertube.com/embed/video/") > -1) {
                                if(iframe.indexOf("autoplay=") == -1) {
                                    addedParameters += "&autoplay=true";
                                }

                                if(iframe.indexOf("?") == -1) {
                                    // REMOVE THE FIRST "&"
                                    addedParameters = addedParameters.substr(1);

                                    // ADD PARAMETERS
                                    // MATCH: youtube.com/embed/(any character except space)(end match on ") - Replace: ($& is the matched value)(add ?)(add the built parameters)
                                    iframe = iframe.replace(/teachertube.com\/embed\/video\/[^\s]+?[^"]+/ig, '$&?' + addedParameters);
                                } else {
                                    // ADD PARAMETERS
                                    // MATCH: youtube.com/embed/(any character except space)(end match on ") - Replace: ($& is the matched value)(add the built parameters)
                                    iframe = iframe.replace(/teachertube.com\/embed\/video\/[^\s]+?[^"]+/ig, '$&' + addedParameters);
                                }
                            }

                            return iframe;
                        }

                        // ADD VIDEO DIALOG
                        $("#pmi-" + PMI).append('' +
                            '
' +
                                '
' +
                                    '
' +
                                        '


' +
                                        addUrlParameters(decodeIframeHtml(mmg.records[recordIndex].videoIframe)) +
                                    '' +
                                    'Close' +
                                '' +
                            '' +
                        '');

                        // FOCUS CLOSE BUTTON
                        $("#pmi-" + PMI + " .mmg-video-close").focus();
                    }
                });

                // CLOSE VIDEO
                $("#pmi-" + PMI).on("click keydown", ".mmg-video-close", function(e) {
                    if(_this.AllyClick(e)) {
                        e.preventDefault();

                        var returnFocusIndex = $(this).attr("data-return-index");

                        // ANIMATE THE VIDEO OUT
                        $("#pmi-" + PMI + " .mmg-video-window").addClass("close-video");

                        setTimeout(function() {
                        	// REMOVE THE VIDEO CONTAINER COMPLETELY
                            $("#pmi-" + PMI + " .mmg-video-window").remove();

                            // FOCUS WATCH VIDEO LINK
                        	$("#pmi-" + PMI + " .article-video-button[data-record-index='" + returnFocusIndex + "']").focus();
                        }, 1000);


                    }

                    if(e.type == "keydown" && e.keyCode == _this.KeyCodes.tab) {
                        e.preventDefault();

                        var videoIframe = $("#pmi-" + PMI + " .mmg-video-viewer iframe")[0];
                        videoIframe.contentWindow.focus();
                    }
                });

                // CAPTURE VIDEO WINDOW FOCUS OUT
                $("#pmi-" + PMI).on("focusout", ".mmg-video-window", function() {
                    $("#pmi-" + PMI + " .mmg-video-close").focus();
                });
            });

            this.MakePanels({
                "Selector": "#sw-content-container1.ui-hp .ui-widget.app.multimedia-gallery, #sw-content-container6.ui-hp .ui-widget.app.multimedia-gallery",
                "RecordsPerPanel": recordsPerPanel
            });

            // NOW BUILD THE SLIDER FUNCTIONALITY
            this.RunArticleSlider({
            	"Selector": "#sw-content-container1.ui-hp .ui-widget.app.multimedia-gallery, #sw-content-container6.ui-hp .ui-widget.app.multimedia-gallery"
            });
        },

        "RunArticleSlider": function(props) { // props.Selector
        	$(props.Selector).csArticleSlider({
                "viewerNum": [1, 1, 1, 1, 1], // NUMERICAL - [960 view, 768 view, 640 view, 480 view, 320 view]
                "transitionSpeed": .5, // SECONDS
                "slideFullView": true,
                "extend": function(config, element, ArticleSlider) {}
            });

            $(".cs-article-slider-notifications", props.Selector).remove();
            $(".cs-article-slider-control.back", props.Selector).wrap('
');
            $(".cs-article-slider-control.next", props.Selector).wrap('
');

        },

        "MakePanels": function(props) { // props.Selector, props.RecordsPerPanel
        	var _this = this;

        	$(props.Selector).each(function() {
                $(".ui-articles > li", this).addClass('record');
                var panelNum = Math.ceil($(".ui-articles > li", this).length / props.RecordsPerPanel);
                for(var i = 1; i <= panelNum; i++) {
					$(".ui-articles", this).attr("data-breakpoint", _this.GetBreakPoint).append("
");

                    if(i == panelNum) {
                    	$(".ui-articles > li:not(.panel)",this).prependTo($(".ui-articles li.panel-"+ i + " ul", this));
                    }
                    else {
                    	$(".ui-articles > li:nth-child(-n+" + props.RecordsPerPanel + ")",this).prependTo($(".ui-articles li.panel-"+ i + " ul", this));
                    }
                }
            });

            this.ArticleSliderWatchMobile({
            	"Selector": props.Selector
            });
        },

        "ArticleSliderWatchMobile": function(props) { // props.Selector
        	var _this = this;
            var recordsPerPanel = 0;

            if($(".hp").length) {
                $(window).resize(function() {
                    switch(_this.GetBreakPoint()) {
                        case "desktop":
                            recordsPerPanel = 6;
                        break;

                        case "768":
                        case "640":
                            recordsPerPanel = 4;
                        break;

                        case "480":
                        case "320":
                            recordsPerPanel = 2;
                        break;
                    }

                    if(_this.GetBreakPoint() != $(".ui-articles", props.Selector).attr("data-breakpoint")) {
                        _this.DestroySliderPanels({
                            "Selector": props.Selector
                        });

                        _this.MakePanels({
                            "Selector": props.Selector,
                            "RecordsPerPanel": recordsPerPanel
                        });

                        $(".ui-articles", props.Selector).attr("data-breakpoint", _this.GetBreakPoint());
                    }
                });
            }
        },

        "DestroySliderPanels": function(props) { // props.Selector
        	$(props.Selector).each(function() {
            	var thisApp = this;

            	$(".ui-articles > li.panel", this).each(function() {
                	$(".ui-articles", thisApp).append($(" > ul", this).html());
                });

                $(".ui-articles > li.panel", this).remove();
            });
        },

        "MoveElements": function() {

        	//MOVE CALENDAR LINK TO UPCOMING EVENT HEADER
        	var eventHeader = $('.upcomingevents .ui-widget-header');
        	$('.hp .view-calendar-link').appendTo(eventHeader);

            //CHECK FOR DESKTOP
            if(this.GetBreakPoint() !== 'desktop') {
            	$('#gb-enroll').prependTo('main'); //MOVE ENROLL BUTTON
                $('#bb-footer-links').insertBefore('#footer-last-sec-links'); //MOVE BB FOOTER LINK

            }

            else {
            	$('#gb-enroll').appendTo('#gb-main-header .gb-wrap'); //MOVE ENROLL BUTTON
                $('#bb-footer-links').appendTo('#gb-schoolwires-footer'); //MOVE BB FOOTER LINK
            }

            if(this.GetBreakPoint() == 768 || this.GetBreakPoint() == 640) {
            	$('.gb-footer-quicklinks').insertAfter('#gb-footer-contact');
            }

            //MOVE FOOTER ADS
            if(this.GetBreakPoint() == 480 || this.GetBreakPoint() == 320) {
            	$('#gb-footer-ads').prependTo('.gb-footer-quicklinks');
            }

            else {
            	$('#gb-footer-ads').prependTo('#footer-last-sec');
            }

            //MOVE SITEMAP AND ACCESSABILITY
            if(this.GetBreakPoint() == 320) {
            	$('#top').parent().remove();
                $('#footer-last-sec-links').prependTo('#gb-schoolwires-footer');


            }

            //MOVE VIEW CALENDAR LINK
            if(this.GetBreakPoint() == 640 || this.GetBreakPoint() == 480 || this.GetBreakPoint() == 320) {
            	$('.hp .view-calendar-link').prependTo('.upcomingevents .ui-widget-detail');
            }
        },

        "GetBreakPoint":function() {
        	return window.getComputedStyle(document.querySelector("body"), ":before").getPropertyValue("content").replace(/"|'/g, "");/*"*/
        }
    };

