link.script:
  $('#change_language_form select').each(function () {

  		var $this = $(this),
  			 numberOfOptions = $(this).children('option').length;

  		$this.addClass('s-hidden');

  		$this.wrap('
');

  		$this.after('
');

  		var $styledSelect = $this.next('div.styledSelect');

  		$styledSelect.text($this.children('option').eq(0).text());
  		$styledSelect.text($this.children('option:selected').text());



  		var $list = $('', {
  			 'class': 'options'
  		}).insertAfter($styledSelect);

  		for (var i = 0; i < numberOfOptions; i++) {
  			 $('
', {
  					text: $this.children('option').eq(i).text(),
  					rel: $this.children('option').eq(i).val()
  			 }).appendTo($list);
  		}

  		var $listItems = $list.children('li');

  		$styledSelect.click(function (e) {
  			 e.stopPropagation();
  			 $('div.styledSelect.active').each(function () {
  					$(this).removeClass('active').next('ul.options').hide();
  					$(this).parent().parent().removeClass('focus');
  			 });
  			 $(this).toggleClass('active').next('ul.options').toggle();
  			 $(this).parent().parent().toggleClass('focus');
  		});
  		$styledSelect.parent().prev().click(function (e) {
  			 e.stopPropagation();
  			 $(this).parent().toggleClass('focus').find('ul.options').toggle();;
  		});
  		var indexselected = $this.children('option:selected').index();

  		$listItems.eq(indexselected).addClass('selected');
  		$listItems.click(function (e) {

  			 e.stopPropagation();

  			 $('.options li').removeClass('selected');
  			 $(this).addClass('selected');
  			 $styledSelect.text($(this).text()).removeClass('active');
  			 $styledSelect.parent().parent().removeClass('focus');

  			 $this.val($(this).attr('rel'));
  			 $list.hide();
  			 $this.trigger("change");
  		});

  		$(document).click(function () {
  			 $styledSelect.removeClass('active');
  			 $styledSelect.parent().parent().removeClass('focus');
  			 $list.hide();
  		});
  		return $(this);
   });

$(document).ready(function () {
    $('.slider-container').bxSlider({
        mode: 'horizontal'
        , auto: true
        , pause: 3500
        , adaptiveHeight: true
        , speed: 1500
        , onSliderLoad: function () {
            if ($(window).width() > 1040) {
                var sliderheight = $('#slider').height();
                var headerheight = $('#header').height();
                var windowheight = $(window).height();
                var leftSliderHeight = $('#slider .slider-info').height();
                var imgheight = windowheight - headerheight - 10;
                $('.slider-container img, #slider,.slider-container  .bx-wrapper .bx-viewport').css("max-height", imgheight);
                $('.slider-container img, #slider, .slider-container  .bx-wrapper .bx-viewport').css("min-height", leftSliderHeight + 60);
                //$('#left-nav .menu-wrap').css("height", imgheight);
            }
            else {
                $('.slider-container img, #slider,.slider-container  .bx-wrapper .bx-viewport').css("max-height", "100%");
                $('.slider-container img, #slider, .bx-wrapper .bx-viewport').css("min-height", 90);
                //$('#left-nav .menu-wrap').css("height", "auto");
            }
            $(window).resize(function () {
                if ($(window).width() > 1040) {
                    var sliderheight = $('#slider').height();
                    var headerheight = $('#header').height();
                    var windowheight = $(window).height();
                    var leftSliderHeight = $('#slider .slider-info').height();
                    var imgheight = windowheight - headerheight - 10;
                    $('.slider-container img, #slider, .slider-container  .bx-wrapper .bx-viewport').css("max-height", imgheight);
                    $('.slider-container img, #slider, .slider-container  .bx-wrapper .bx-viewport').css("min-height", leftSliderHeight + 60);
                    // $('#left-nav .menu-wrap').css("height", imgheight);
                }
                else {
                    $('.slider-container img, #slider,.slider-container  .bx-wrapper .bx-viewport').css("max-height", "100%");
                    $('.slider-container img, #slider, .slider-container  .bx-wrapper .bx-viewport').css("min-height", 90);
                    //$('#left-nav .menu-wrap').css("height", "auto");
                }
            });
        }
    });
});

