                "type": 1,
                "languages": [ // ["ENGLISH LANGUAGE NAME", "TRANSLATED LANGUAGE NAME", "LANGUAGE CODE"] - IF TRANSLATED LANGUAGE NAME IS EMPTY, THE ENGLISH NAME WILL BE USED
                    ["Arabic", "", "ar"],
                    ["Chinese Simplified", "", "zh-CN"],
                    ["Chinese Traditional", "", "zh-TW"],
                    ["German", "Deutsche", "de"],
                    ["Italian", "italiano", "it"],
                    ["Japanese", "", "ja"],
                    ["Korean", "", "ko"],
                    ["Portuguese", "portugus", "pt"],
                    ["Russian", "", "ru"],
                    ["Spanish", "Espaol", "es"],
                ],
            	"advancedOptions": {
                    "addMethod": "append",
                    "customElement": {
                        "useCustomElement": true,
                        "translateItemsList": true,
                        "customElementMarkup": "[$CreativeTranslateListItems$]"
                    }
                },
                "translateLoaded": function() {
                    $(".cs-mystart-dropdown.translate .cs-dropdown ul").addClass("cs-dropdown-list cs-html-list-reset").attr("id", "cs-translate-list");
                }
            });

            this.MyStartDropdownActions({
            	"dropdownParent": ".cs-mystart-dropdown",
                "dropdownSelector": ".cs-selector",
                "dropdown": ".cs-dropdown",
                "dropdownList": ".cs-dropdown-list",
                "openDrowpdownList" : ".cs-mystart-dropdown.open"
            });
