        "MyStartDropdownActions": function(params) {
        	// FOR SCOPE
            var template = this;

            /* ASSUMES THE FOLLOWING STRUCTURE



User Options




Link



            */

        	var dropdownParent = params.dropdownParent;
            var dropdownSelector = params.dropdownSelector;
            var dropdown = params.dropdown;
            var dropdownList = params.dropdownList;
            var openDrowpdownList = params.openDrowpdownList;

            // MYSTART DROPDOWN SELECTOR CLICK EVENT
            $(document).on("click", dropdownSelector, function(e) {
                e.preventDefault();
                var thisLabel = $(this).attr("aria-label");

                $(dropdownSelector).not(this).each(function(){
                    if($(this).parent().hasClass("open")){
                        thisLabel = thisLabel.replace("Collapse", "Expand");
                        $("+ " + dropdownList + " a").attr("tabindex", "-1");
                        $(this).attr({"aria-expanded": "false", "aria-label": thisLabel}).parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing"); 
                    } 
                });

                if($(this).parent().hasClass("open")){
                	thisLabel = thisLabel.replace("Collapse", "Expand");
                	$("+ " + dropdownList + " a").attr("tabindex", "-1");
                    $(this).attr({"aria-expanded": "false", "aria-label": thisLabel}).parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing"); 
                } else {
                	thisLabel = thisLabel.replace("Expand", "Collapse");
                    $(this).attr({"aria-expanded": "true", "aria-label": thisLabel}).parent().addClass("open").find(dropdown).attr("aria-hidden","false").slideDown(300, "swing", function(){
                        $(dropdownList + " li a", this).eq(0).attr("tabindex", "0").focus();
                    });
                }
        	});

            $(document).on("focusout", dropdownParent, function(e) {
                var thisLabel = $(dropdownSelector, this).attr("aria-label");
                var $elem = $(this);

                setTimeout(function() {
                    var hasFocus = !! ($elem.find(':focus').length > 0);
                    if (! hasFocus) {
                		thisLabel = thisLabel.replace("Collapse", "Expand");
                		$(dropdownSelector + "+ " + dropdownList + " a").attr("tabindex", "-1");
                    	$(dropdownSelector).attr({"aria-expanded": "false", "aria-label": thisLabel}).parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing"); 
                    }
                }, 10);
            });

            // MYSTART DROPDOWN SELECTOR KEYDOWN EVENTS
            $(document).on("keydown", dropdownSelector, function(e) {
                // CAPTURE KEY CODE
                switch(e.keyCode) {
                    // CONSUME LEFT AND UP ARROWS
                    case template.KeyCodes.enter:
                    case template.KeyCodes.space:
                        e.preventDefault();
                        var thisLabel = $(this).attr("aria-label");

                        // IF THE DROPDOWN IS OPEN, CLOSE IT
                        if($(dropdownParent).hasClass("open")){
                        	thisLabel = thisLabel.replace("Collapse", "Expand");
                        	$("+ " + dropdown + " " + dropdownList + " a").attr("tabindex", "-1");
                            $(this).attr({"aria-expanded": "false", "aria-label": thisLabel}).parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing"); 
                        } else {
                        	thisLabel = thisLabel.replace("Expand", "Collapse");
                            $(this).attr({"aria-expanded": "true", "aria-label": thisLabel}).parent().addClass("open").find(dropdown).attr("aria-hidden", "false").slideDown(300, "swing", function(){
                            	$(dropdownList + " li a", this).eq(0).attr("tabindex", "0").focus();
                            });
                        }

                    break;

                    // CONSUME TAB KEY
                    case template.KeyCodes.tab:
                        $("+ " + dropdown + " " + dropdownList + " a").attr("tabindex", "-1");
                        $(this).attr("aria-expanded", "false").parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing");
                    break;

                    // CONSUME LEFT AND UP ARROWS
                    case template.KeyCodes.down:
                    case template.KeyCodes.right:
                        e.preventDefault();

                        $("+ " + dropdown + " " + dropdownList + " a").attr("tabindex", "-1");
                        $("+ " + dropdown + " " + dropdownList + " li > a", this).attr("tabindex", "0").focus();

                    break;
                }
            });

            $(document).on("keydown", dropdownList + " li a", function(e) {
                // CAPTURE KEY CODE
                switch(e.keyCode) {
                    // CONSUME LEFT AND UP ARROWS
                    case template.KeyCodes.left:
                    case template.KeyCodes.up:
                        e.preventDefault();

                        // IS FIRST ITEM
                        if($(dropdownList + " li a").index(this) == 0) {
                            // FOCUS DROPDOWN BUTTON
                            $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                            $(this).closest(dropdownParent).find(dropdownSelector).focus();
                        } else {
                            // FOCUS PREVIOUS ITEM
                            $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                            var index = $(dropdownList + " li a").index(this)-1;
                        	$(dropdownList + " li a").eq(index).attr("tabindex", "0").focus();
                        }
                    break;

                    // CONSUME RIGHT AND DOWN ARROWS
                    case template.KeyCodes.right:
                    case template.KeyCodes.down:
                        e.preventDefault();

                        // IS LAST ITEM
                        if($(dropdownList + " li a").index(this) == $(this).closest(dropdownList).find("a").length-1) {
                            // FOCUS FIRST ITEM
                            $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                            $(this).closest(dropdownList).find("a").eq(0).attr("tabindex", "0").focus();
                        } else {
                            // FOCUS NEXT ITEM
                            $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                           	var index = $(dropdownList + " li a").index(this)+1;
                        	$(dropdownList + " li a").eq(index).attr("tabindex", "0").focus();
                        }
                    break;

                    // CONSUME TAB KEY
                    case template.KeyCodes.tab:
                    	e.preventDefault();

                    	if(e.shiftKey) {
                        	// IS FIRST ITEM
                            if($(this).closest(dropdownList).find("a").index(this) == 0) {
                                // FOCUS DROPDOWN BUTTON
                                $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                                $(this).closest(dropdownParent).find(dropdownSelector).focus();
                            } else {
                                // FOCUS PREVIOUS ITEM
                                $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                                $(this).parent().prev("li").find("> a").attr("tabindex", "0").focus();
                            }
                        } else {
                        	// IS LAST ITEM
                            var index = $(this).closest(dropdownList).find("a").index(this);

                            if($(this).closest(dropdownList).find("a").index(this) == $(this).closest(dropdownList).find("a").length-1) {
                                // FOCUS FIRST ITEM
                                $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                                $(this).closest(dropdownList).find("a").eq(0).attr("tabindex", "0").focus();
                            } else {
                                // FOCUS NEXT ITEM
                                $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                                $(this).closest(dropdownList).find("a").eq(index+1).attr("tabindex", "0").focus();
                            }
                        }
                    break;

                    // CONSUME HOME KEY
                    case template.KeyCodes.home:
                        e.preventDefault();

                        // FOCUS FIRST ITEM
                        $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                        $(this).closest(dropdownList).find("li:first-child > a").attr("tabindex", "0").focus();
                    break;

                    // CONSUME END KEY
                    case template.KeyCodes.end:
                        e.preventDefault();

                        // FOCUS LAST ITEM
                        $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                        $(this).closest(dropdownList).find("li:last-child > a").attr("tabindex", "0").focus();
                    break;
                }
            });
        },

        "Header": function() {
            // ADD LOGO
            var showLogo = true;
            var logoSrc = $.trim("/cms/lib/CA01902308/Centricity/Template/GlobalAssets/images///logos/district-logo.png");

            if(showLogo) {
            	var srcCheck = logoSrc.indexOf("Faces/default-man.jpg") > -1;

                if((logoSrc != "") && (!srcCheck)) {
                    $("#gb-logo").append('');
                } else {
                    $("#gb-logo").append('');
                }
            }

            // ADD SITENAME
            var siteNameOne = $.trim("Chino Valley");
            var siteNameTwo = $.trim("Unified School District");
            var siteTagline = $.trim("Student Achievement * Safe Schools * Positive School Climate");

            if((siteNameOne == "") && (siteNameTwo == "")) {
                var splitLen = 2;
                var siteName = "Chino Valley Unified School District";
                siteName = siteName.split(" ");
                var siteNameLength = siteName.length;
                if(siteNameLength > 2){
                    siteNameEnd = $.trim(siteName.splice(-splitLen, siteName.length).toString().replace(/,/g, " "));
                } else {
                    siteNameEnd = $.trim(siteName.splice(-1, siteName.length).toString().replace(/,/g, " "));
                }
                siteNameBegin = $.trim(siteName.toString().replace(/,/g, " "));
                $("#gb-sitename").prepend("
" + siteNameBegin + "
" + siteNameEnd + "");
            } else if((siteNameOne != "") && (siteNameTwo == "")) {
                $("#gb-sitename").prepend("
" + siteNameOne + "");
            } else if((siteNameOne == "") && (siteNameTwo != "")) {
                $("#gb-sitename").prepend("
" + siteNameTwo + "");
            } else if((siteNameOne != "") && (siteNameTwo != "")) {
                $("#gb-sitename").prepend("
" + siteNameOne + "
" + siteNameTwo + "");
            }

            if(siteTagline != "") {
            	$("#gb-sitename").append("
" + siteTagline + "");
			}
        },

