        "UpcomingEvents": function() {
            // FOR SCOPE
            var _this = this;   

            if (!$(".upcomingevents .joel-month").length) {
                $(".upcomingevents").modEvents({
                    columns     : "yes",
                    monthLong   : "yes",
                    dayWeek     : "yes"
                });
                eventsByDay(".upcomingevents .ui-articles");
            }

            function eventsByDay(container) {
                var countLi = 0;
                $(".ui-article", container).each(function(){
                    if (!$(this).find("h1.ui-article-title.sw-calendar-block-date").size()){
                        var moveArticle = $(this).html();
                        $(this).parent().prev().children().children().next().append(moveArticle);
                        $(this).parent().remove();
                    };
                });

                $(".sw-calendar-block-date").each(function() {
                	var day = $(".joel-day", this).text();
                    if(day == "Thursday") {
                    	day = day.substr(0, 4);
                    } else {
                    	day = day.substr(0, 3);
                    }
                    $(".joel-day", this).text(day);
                	$(".joel-day", this).appendTo($(this));
                });

                $(".sw-calendar-block-time").each(function() {
                	$(this).html($(this).text().replace(/ AM/g, "AM"));
                    $(this).html($(this).text().replace(/ PM/g, "PM"));
                });

                $(".upcoming-column.right").each(function(){
				    var times = {};
                    $(".sw-calendar-block-time", this).each(function () {
                      var thisVal = $(this).text();
                      if (!(thisVal in times)) {
                      	times[thisVal] = "";
                      } else {
                        $(this).remove();
                      }
                    });
                });
            }
        },

