            function getExtraMenuOptions() {
                var extraItems = "";
                $(".gb-schools-header").each(function() {
                    extraItems += $(this).text();
                    if($(".gb-schools-header").index(this) != $(".gb-schools-header").length-1) {
                        extraItems += ";";
                    }
                });
                return extraItems;
            }

            function getExtraMenuItems() {
                var extraItems = "";
                $(".gb-schools-menu").each(function() {
                    var extraMenuItems = "";
                    var element = $(this);
                    var elementIndex = $(".gb-schools-menu").index(element)+1;
                    $("a", this).each(function() {
                        extraMenuItems += elementIndex+","+$(this).text()+","+$(this).attr("href")+",_self;";
                    });
                    extraItems += extraMenuItems;
                });
                return extraItems;
            }
        },

