			});
		}

			//puts the google translate element on the page
		loadScript("//translate.google.com/translate_a/element.js?cb=googleTranslateElementInit");

	});	

//SKELETON
	function pgScreenSize(){
		var getWidth = $(window).width();

		multimediaRotator();
		if( getWidth >= 1024){
			adjustGlobalLinkLabels();
		}else{
			if( getWidth < 768 ){
				appView('#hp-content');

				if( getWidth < 480 ){
					appView('#slideshow-tabs');
				}
			}
		}

		if( $('.sp').length > 0 ){


        		if( getWidth < 768 ){
				//pagelist accordion classing trigger
				$('.sp.column.one .ui-widget-header').off('click').click(function(e){
					$(this).parent().toggleClass('active-app');
				});
			}else{
				$('.sp.column.one .ui-widget-header').off('click');
			}
        	}
	}

	function multimediaRotator(){
		if(!$("#sw-content-container6.region.ui-hp .rs-gallery-container").length){
			// MULTIMEDIA ROTATOR
			$("#sw-content-container6.ui-hp div.ui-widget.app.cs-rs-multimedia-rotator").csRsMultimediaRotator({
				"imageWidth"              : 815,
		                "imageHeight"             : 420,
		                "efficientLoad"           : "yes",
		                "appHeader"               : "no",
		                "titles"                  : "yes",
		                "captions"                : "yes",
		                "galleryOverlay"          : "no",
		                "linkedElement"           : [], // ["image", "title", "overlay", "all"]
		                "playPause"               : "no",
		                "mainControls"            : "yes", /* Added arrows by designers request - Jeremy 02082016 */
		                "contMoreLink"            : "no",
		                "contMoreLinkText"        : "Read More",
		                "descMoreLink"            : "yes",
		                "descMoreLinkText"        : "Read More",
		                "bullets"                 : "no",
		                "bulletControls"          : "no",
		                "thumbnails"              : "no",
		                "thumbnailWidth"          : 122,
		                "thumbnailHeight"         : 53,
		                "thumbnailSlideDirection" : "horizontal", // horizontal or vertical
		                "hoverPause"              : "yes",
		                "autoRotate"              : "yes",
		                "fullScreenRotator"       : "no", // image dimensions work well at 1500x915
		                "fullScreenBreakpoints"   : ["960", "768", "640", "480", "320"], // OPTIONS ARE ["960", "768", "640", "480", "320"]
		                "fullScreenVideo"         : "no",
		                "transitionType"          : "fade", // fade or slide
		                "transitionSpeed"         : 3,
		                "transitionDelay"         : 8,
		                "onImageLoad"             : function() { },
		                "allImagesLoaded"         : function() { },
		                "onThumbnailLoad"         : function() { },
		                "allThumbnailsLoaded"     : function() { },
		                "onTransitionStart"       : function() { },
		                "onTransitionEnd"         : function() { },
		                "watchVidClick"           : function() { },
		                "closeVidClick"           : function() { },
		                "allLoaded"               : function() { 

		                	$('#hp-slideshow .rs-gallery-desc').css('width', $('#hp-slideshow-outer > .max-width').width() - ($('#slideshow-tabs').width() - 50) ); /* The modifier is 30px to account for tabs overflow which is achieved by width, and 25px to account for god knows what.. */

		                	if( $(window).width() < 1024 && $(window).width() > 480 ){
	                			$('#hp-slideshow-outer .social-media-cont').css( 'top', $('#hp-slideshow').outerHeight() + 80 );
	                		}

		                	$(window).resize(function(e){
		                		$('#hp-slideshow .rs-gallery-desc').css('width', $('#hp-slideshow-outer > .max-width').width() - ($('#slideshow-tabs').width() - 50) );

		                		if( $(window).width() < 1024 && $(window).width() > 480 ){
		                			$('#hp-slideshow-outer .social-media-cont').css( 'top', $('#hp-slideshow').outerHeight() + 80 );
		                		}else{
		                			$('#hp-slideshow-outer .social-media-cont').attr( 'style', '');
		                		}
		                	});

		                	/* Added arrows by designers request - Jeremy 02082016 */
		                	if( $('#hp-slideshow .rs-gallery-control.next').length > 0 ){
		                		$('#hp-slideshow .rs-gallery-control.next, #hp-slideshow .rs-gallery-control.back').appendTo($('#hp-slideshow .rs-gallery-desc')).hide();
		                	}
		                	$('#hp-slideshow').hover(function(e){
		                		$('#hp-slideshow .rs-gallery-control.next, #hp-slideshow .rs-gallery-control.back').show(400);
		                	}, function(e){
		                		$('#hp-slideshow .rs-gallery-control.next, #hp-slideshow .rs-gallery-control.back').hide(400);
		                	});
		                }
			});
		}
	}

	function modEvents(){
	    if (!$(".upcomingevents .joel-month").length) {
	        $(".upcomingevents").modEvents({
	            columns     : "yes",
	            monthLong   : "yes",
	            dayWeek     : "yes"
	        });
	        eventsByDay(".upcomingevents .ui-articles");
	    }
	}

	function eventsByDay(container) {
		var countLi = 0;
		$(".ui-article", container).each(function(){
			if (!$(this).find("h1.ui-article-title.sw-calendar-block-date").size()){
			    var moveArticle = $(this).html();
			    $(this).parent().prev().children().children().next().append(moveArticle);
			    $(this).parent().remove();
			};
		});

		$(".ui-article", container).each(function(){
			var newDateTime = $('.upcoming-column.left h1', this).html().toLowerCase().split("");
			var newDate = '';
			var dateIndex = ((newDateTime.length > 2) ? 2 : 1); //if the dayWeek is set to yes we need to account for that in our array indexing

			//if we have the day of the week make sure to include it.
			if( dateIndex > 1 ){ 
				newDate += newDateTime[0] + "";
			}

			//wrap the date in a new tag
			newDate += '' + newDateTime[dateIndex] + '';

			//add in the month
			newDate += newDateTime[dateIndex - 1] + ''; //the month is always the in the left array position to the date


			//append the date and month back into their columns
			$('.upcoming-column.left h1', this).html(newDate);	


			//add an ALL DAY label if no time was given
			$('.upcoming-column.right .ui-article-description', this).each(function(){
				if( $('.sw-calendar-block-time', this).length < 1 ){ //if it doesnt exist add it
					$(this).prepend('ALL DAY');
				}
			});
		});

		$('.hp.column.two .upcomingevents .ui-articles').after('');
		var newList = $('.hp.column.two .upcomingevents .new-list');
		var monthsHit = [];

		$('.hp.column.two .upcomingevents .ui-articles:not(.new-list) > li').each(function(k, v){
			var eventMonth = $('.joel-month', v).html();
			if( monthsHit.indexOf(eventMonth) < 0 || $('li', newList).length <= 0 ){
				monthsHit.push(eventMonth);
				newList.append('
\

' + eventMonth + '\
					\
				');
			}

			$('.joel-month, .joel-day', v).remove();
			$('.upcomingevents .ui-articles.new-list li[data-for="' + eventMonth + '"] .month-events').append('
' + $(v).html() + '');
		});

		$('.hp.column.two .upcomingevents .ui-articles:not(.new-list)').remove();
	}

	function appView( parentSelector ){
		//DOC dont add app into accordion if it doesnt have a header
		$("div.ui-widget.app", parentSelector).each(function() {
			if($(".ui-widget-header", this).hasClass("ui-helper-hidden") || jQuery.trim($(".ui-widget-header h1", this).text()) == "") {
				$(this).addClass("no-header");
			}else{
				if( $('.ui-widget-header h1 span.accordion-handle', this).length <= 0){
					$('.ui-widget-header h1', this).append('+');
				}
			}
		});

		//DOC accordion header click
		$("div.ui-widget-header", parentSelector).off('click').click(function(){
			if( $(this).parent().hasClass('active') ){
				$(this).parent().removeClass('active');
			}else{
				var clickedApp = $(this).parent().parent().attr("id");

				$("div.ui-widget.app", parentSelector).removeClass("active");
				$(this).parent().addClass("active");

				smoothScroll("#" + clickedApp, 10);
			}
		});
	}	

