        "ChannelBar": function() {
        	var _this = this;
        	var one = "", two = "";

            $('ul.sw-channel-list li.hidden-channel').remove();

            $(".sw-channel-dropdown a").attr("tabindex", "-1");

            //CUSTOMIZE CHANNEL BAR DROPDOWN ON HOVER

            $('#sw-channel-list-container').on('mouseenter',function() {
            	if(!$('#sw-channel-list-container').hasClass('open')) {
                	one = setTimeout(function() {
                		$('#sw-channel-list-container').addClass('open');
                    }, 200)
                    two = setTimeout(function() {
                    	if($('#sw-channel-list-container').hasClass('open')) {
            				$('ul.sw-channel-dropdown').slideDown('fast');
                        }
            		}, 500)
                }
             }).on('mouseleave', function() {
             		clearTimeout(one);
                    clearTimeout(two);

                    $('ul.sw-channel-dropdown').stop(true,true).fadeOut('fast');
                    setTimeout(function() {
                		$('#sw-channel-list-container').removeClass('open');
                    }, 200)
             });

             $('.sw-channel-item > a').on('focus',function() {
             	openChannelDropdown();
             }).on('blur', function() {
             	closeChannelDropdown();
             });

             function openChannelDropdown() {
             	$("~ .sw-channel-dropdown a").attr("tabindex", "-1");
            	if(!$('#sw-channel-list-container').hasClass('open')) {
                	one = setTimeout(function() {
                		$('#sw-channel-list-container').addClass('open');
                    }, 200)
                    two = setTimeout(function() {
                    	if($('#sw-channel-list-container').hasClass('open')) {
            				$('ul.sw-channel-dropdown').slideDown('fast', function() {
                            	$('#sw-channel-list-container').addClass('force');
                            });
                        }
            		}, 500);
                }
             }

             function closeChannelDropdown() {
                  $("~ .sw-channel-dropdown a").attr("tabindex", "-1");
                  var $elem = $('#sw-channel-list-container');
                  setTimeout(function() {
                     var hasFocus = !! ($elem.find(':focus').length > 0);
                     if (! hasFocus) {
                          clearTimeout(one);
                          clearTimeout(two);

                          $('ul.sw-channel-dropdown').stop(true,true).fadeOut('fast', function() {
                              $('#sw-channel-list-container').removeClass('force');
                          });
                          setTimeout(function() {
                              $('#sw-channel-list-container').removeClass('open');
                          }, 200);
                     }
                 }, 10);
             }

             $(".sw-channel-dropdown a").focus(function() {
             	$(this).attr("tabindex", "0");
             }).blur(function() {
             	$(this).attr("tabindex", "-1");
             }).keydown(function(e) {
             	if($(this).closest(".sw-channel-item").is(":last-child")) {
                	if(e.keyCode == _this.KeyCodes.tab && !e.shiftKey) {
                    	e.preventDefault();
                        closeChannelDropdown();
                        $("#gb-global-icons a:first").focus();
                    }
                }

                if(e.keyCode == _this.KeyCodes.tab && e.shiftKey) {
                    e.preventDefault();
                    var sectionLink = this;
                    setTimeout(function() {
                    	$(sectionLink).closest(".sw-channel-item").find("> a").focus();
                    }, 10);
                }
             });

             //NAVIGATE CHANNEL BAR FROM PARENT
             $('.sw-channel-item > a').on('keydown', function(e){

                // CONTROL TAB+SHIFT KEY
             	if(e.shiftKey && e.which == 9) {
                	e.preventDefault();
                	if($(this).parent().is(":first-child")) {
                    	$('.user-options .cs-selector').focus();

                    } else {
                    	$(this).parent().prev("li").find('> a').focus();
                    }
                }

                // CONSUME TAB ONLY KEY
             	if(e.which == 9 && !e.shiftKey) { 
                	if(!$(this).parent().is(":last-child")) {
                		e.preventDefault();
                  		//$(this).parent().find('a').focus();
                    	$(this).parent().next("li").find('> a').focus();
                    }
             	}

                // CONSUME DOWN AND RIGHT KEY
				if(e.which == 40 || e.which == 39) {
                	e.preventDefault();
                	$(this).parent().find('ul.sw-channel-dropdown li a:eq(0)').focus();
                }
             });

             //NAVIGATE CHANNEL BAR WITHIN DROPDOWN

             //SET HEIGHT OF CHANNELBAR DROPDOWN
             var maxHeight = 0;
             $('#sw-channel-list-container').prepend('');

              $('.sw-channel-dropdown').each(function(){
                 maxHeight = $(this).height() > maxHeight ? $(this).height() : maxHeight;

                 $('#sw-channel-list-container .bg').css('height', maxHeight + 45);



              });


        },

