                        	$('button.mmg-control').prependTo('.mmg-slides-outer');
                        }



                    }, // props.element, props.mmgRecords
                    "onWindowResize": function(props) {

                        //MOVE PLAY/PAUSE BUTTON ON SLIDSHOW
                        if(this.GetBreakPoint() == 480 || this.GetBreakPoint() == 320) {
                        	if(!$('.mmg-slides-outer #hp-slideshow button.mmg-control').length) {
