        "Search": function() {
        	_this = this;
            // //DON"T ALLOW USER TO SUBMIT AN EMPTY FORM
            $('#sw-search-input').off();
            $('#gb-search-input').on('keydown', function(e){
                if(e.which == 13){//Enter key pressed
                    e.preventDefault();
                     if($.trim($("#gb-search-input").val()) != "What can I help you find?" && $.trim($("#gb-search-input").val()) != "") {


                        window.location.href = "/site/Default.aspx?PageType=6&SiteID=4&SearchString=" + $("#gb-search-input").val();
                    }

                } 


            });

            $("#sw-search-button").off().removeAttr("onclick").attr("href", "#");
            $("#gb-search-submit").on("click keydown", function(e) {
                if(_this.AllyClick(e)) {
                    e.preventDefault();

                    if($.trim($("#gb-search-input").val()) != "What can I help you find?" && $.trim($("#gb-search-input").val()) != "") {
                        window.location.href = "/site/Default.aspx?PageType=6&SiteID=4&SearchString=" + $("#gb-search-input").val();
                    }
                }


            });

            $('#search-btn a').on('click', function() {
            	if($(this).attr("aria-expanded") == "false") {
                	$(this).attr('aria-expanded', true).attr('aria-label', 'Close Search Form');
                    $('#gb-search-form-bg').addClass('showing');
                    $('#search-btn').addClass('isOpen');
                    $('#gb-search-input').focus();
                }else {
                	$(this).attr('aria-expanded', false).attr('aria-label', 'Open Search Form');
                    $('#gb-search-form-bg').removeClass('showing');
                    $('#search-btn').removeClass('isOpen');

                }

            });

            //SEARCH TEXT
            $("#gb-search-input").focus(function() {
                if($(this).val() == "What can I help you find?") {
                    $(this).val("");
                }
            }).blur(function() {
                if($(this).val() == "") {
                    $(this).val("What can I help you find?");
                }
            });


            //CONTROL FOCUS WHEN PRESSING SHIFT + TAB WHEN SEARCH IS OPEN
            $('#gb-search-submit').on('keydown', function(e) {
            		if(e.shiftKey && e.which === 9) {
                    	e.preventDefault();
						$('#gb-search-input').focus();
                    }
            });

            $('#gb-search-input').on('keydown', function(e) {
            		if(e.shiftKey && e.which === 9) {
                    	e.preventDefault();
						$('#search-btn').find('a').focus();
                    }
            });




        },

