        "MoveElements" :function() {
        	var _this = this;
        	//PARALLAX EFFECT FOR FIRST CONTENT SECTION 
            $(window).on('scroll', function () {
             	if(_this.GetBreakPoint() == 'desktop') {

                	var $el = $('#hp-content-section-one');

                    var scroll = $(document).scrollTop();
                    $el.css({
                        'background-position':'50% '+(-.4*scroll)+'px'
                    });
                }

                if(_this.GetBreakPoint() !== 'desktop') {
                    var $el = $('#hp-content-section-one');
                    $el.css({
                        'background-position':'0'
                    });
                }
            });

            if(_this.GetBreakPoint() == 'desktop' || _this.GetBreakPoint() == '768' || _this.GetBreakPoint() == '640') {
            	$('.cs-fullscreen-video-buttons').show();
            } else {
            	$('.cs-fullscreen-video-buttons').hide();
            }



            //HIDE SEARCH TEXT FLYOUT ON MOBILE
            if(this.GetBreakPoint() !== 'desktop') {
                $('#search-btn .text').hide();
            }else {
            	$('#search-btn .text').show();
            }

        	//MOVE MORE LINK ON HEADLINES SECTION 2
        	if(this.GetBreakPoint() == 480 || this.GetBreakPoint() == 320) {
            	$('.hp .headlines .ui-read-more').appendTo('#hp-headlines-section-two div.ui-widget.app .ui-widget-header');
            }else {
            	$('.hp .headlines .ui-read-more').prependTo('#hp-headlines-section-two div.ui-widget.app .ui-widget-header');
            }

            if(this.GetBreakPoint() == 320) {
            	$('.hp .upcomingevents .view-calendar-link').appendTo('.upcomingevents .ui-widget-header');
            }else {
            	$('.hp .upcomingevents .view-calendar-link').prependTo('.app.upcomingevents');
            }




        },

        "GetBreakPoint":function() {
        	return window.getComputedStyle(document.querySelector("body"), ":before").getPropertyValue("content").replace(/"|'/g, "");/*"*/
        }
    };

