    $(function() {
    	CreativeTemplate.Init();
    });

    var CreativeTemplate = {
    	// PROPERTIES
        "KeyCodes": { "tab": 9, "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40 },
        "IsMyViewPage": false, // UPDATES IN SetTemplateProps METHOD

        // METHODS
        "Init": function() {
            // FOR SCOPE
            var _this = this;

            this.SetTemplateProps();
            this.JsMediaQueries();
            this.Header();
            this.MyStart();            
            this.ChannelBar();
            this.Body();
            this.UpcomingEvents();
            this.Footer();
            this.Slideshow();
            this.StreamingVideo();
            this.RsMenu();
            this.Search();

            csGlobalJs.OpenInNewWindowWarning();

            $(window).load(function(){ _this.WindowLoad(); });
            $(window).resize(function(){ _this.WindowResize(); });
            $(window).scroll(function(){ });
        },

        "SetTemplateProps": function() {
        	// MYVIEW PAGE CHECK
            if($("#pw-body").length) this.IsMyViewPage = true;
        },

        "WindowLoad": function() {
        	this.MegaMenu();
            this.DynamicBackgrounds();
        },

        "WindowResize": function() {
            this.JsMediaQueries();
        },

        "JsMediaQueries": function() {
            switch(this.GetBreakPoint()) {
                case "desktop": 

                break;
                case "768":
                case "640":
                case "480":
                case "320":

                break;
            }
        },

       "DynamicBackgrounds": function() {
        	//Variables
            var regionDBackgroundSrc = $.trim('/cms/lib/CA01902709/Centricity/Template/GlobalAssets/images///backgroundphotos/RSD-background-1.png');
            var srcCheck2 = regionDBackgroundSrc.indexOf("Faces/default-man.jpg") > -1;

            var taglineBGSrc = $.trim('/cms/lib/CA01902709/Centricity/Template/GlobalAssets/images///backgroundphotos/RSD-tagline-photo.png');
            var srcCheck3 = taglineBGSrc.indexOf("Faces/default-man.jpg") > -1;

            var styles = "";

            if((regionDBackgroundSrc != "") && (!srcCheck2)) {
                styles += '.hp-row.three:before { \n'+   
                              "\t background: url('"+regionDBackgroundSrc+"') no-repeat fixed; \n"+
                              "\t background-size: cover; \n"+
                          '}\n';
            } else {
                styles += '.hp-row.three:before { \n'+   
                              "\t background: url('/cms/lib/CA01902709/Centricity/Template/17/hp-region-d-background.jpg') top center no-repeat;\n"+
                              "\t background-size: cover; \n"+
                          '}\n';
            }

            if((taglineBGSrc != "") && (!srcCheck3)) {
                styles += '#hp-tagline-outer { \n'+   
                              "\t background: url('"+taglineBGSrc+"') no-repeat fixed; \n"+
                              "\t background-size: cover; \n"+
                          '}\n';
            } else {
                styles += '#hp-tagline-outer { \n'+   
                              "\t background: url('/cms/lib/CA01902709/Centricity/Template/17/hp-tagline-background.jpg') top center no-repeat;\n"+
                              "\t background-size: cover; \n"+
                          '}\n';
            }


            styles += '@media (max-width: 1023px) {\n'+
            		  		'.hp-row.one{ \n'+   
                                "\t background: url('"+$("#hp-slideshow img").eq(0).attr("src")+"') top center no-repeat;\n"+
                                "\t background-size: cover; \n"+
                            '}\n';
            		  '}\n';

            $('head').append(
                $('', {
                    id: 'cs-dynamic-background-styles',
                    html: styles
                })
            );

        },        

