            // ACCESSIBILITY         
            $(document).on("keydown", ".cs-mega-menu-wrapper a", function(e) { 
                switch(e.keyCode) {
                    // CONSUME LEFT AND UP ARROWS
                    case _this.KeyCodes.left:
                    case _this.KeyCodes.up:
                        e.preventDefault();
                        if($(".mega-channel a").index(this) == 0) {
                        	$(this).parents(".sw-channel-item").find("> a").focus();
                        } else {
                        	var index = $(".mega-channel a").index(this)-1;
                        	$(".mega-channel a").eq(index).focus();
                        }
                    break;             
                    // CONSUME RIGHT AND DOWN ARROWS
                    case _this.KeyCodes.right:
                    case _this.KeyCodes.down:
                        e.preventDefault();
                        if($(".mega-channel a").index(this) == $(".mega-channel a").length) {
                        	$(this).parents(".sw-channel-item").find("> a").focus();
                        } else {
                            var index = $(".mega-channel a").index(this)+1;
                            $(".mega-channel a").eq(index).focus();
                        }
                    break;
                    case _this.KeyCodes.tab:
                   		e.preventDefault();
                        var index = $(".mega-channel a").index(this)+1;
                        $(".mega-channel a").eq(index).focus();
                    break;
                  }
            });
        },        

