                	$(".ui-widget-header", this).addClass("flex flex-items-center flex-justify-space");
                	$(".ui-read-more", this).appendTo($(".ui-widget-header", this));
                }
                if($(".view-calendar-link", this).length) {
                	$(".ui-widget-header", this).addClass("flex flex-items-center flex-justify-space");
                	$(".view-calendar-link", this).appendTo($(".ui-widget-header", this));
                }
            });

            var showTaglineLogo = true;
            var taglineLogoSrc = $.trim("/cms/lib/CA01902709/Centricity/Template/GlobalAssets/images///backgroundphotos/hp-tagline-image.png");

