                    $("#hp-tagline").prepend('');
                } else {
                    $("#hp-tagline").prepend('');
                }
            }

            $(".hp-row.three div.ui-widget.app.navigation li a").hover(function() {
            	$(this).parent().addClass("hover");
            }, function() {
            	$(this).parent().removeClass("hover");
            }).focus(function() {
            	$(this).parent().addClass("hover");
            }).blur(function() {
            	$(this).parent().removeClass("hover");
            });

            if($(".sp").length) {
              //GRAB RANDOM IMAGE FOR SUBPAGE
              $.ajax({
                  type: "GET",
                  url: "/site/handlers/getimagesxml.ashx",
                  data: "SiteID=4&base_folder=Subpage_Banners",
                  dataType: "xml",
                  cache: false,
                  success: function(data) {
                      var imgNum = $(data).find("img").length;
                      var randomImg = Math.floor(Math.random() * imgNum);
                      var randomImgSrc = "/cms/lib/CA01902709/Centricity/Domain/4/Subpage_Banners/" + $(data).find("img").eq(randomImg).attr("src");

                      $("#sp-banner").append("");
                      $('#sp-banner img').attr('alt', '').attr('role', 'presentation');
                      csGlobalJs.ResetRedirectToPath();
                  }
              });
            }
