// formats the numbers - 12,345 becomes 12 345
function whoNumberFormat(number){
	return number.toString().replace(/\B(?=(\d{3})+(?!\d))/g, " ");
};

// 123.345678 becomes 123.34
function whoNumberRound(number){
	return Math.round((number + Number.EPSILON) * 100) / 100
};

var options = { year: 'numeric', month: 'long', day: 'numeric' ,hour: '2-digit', minute: '2-digit', timeZoneName: 'short'};

function buildTotalCasesBox (totalConfirmedCases) {
    if (totalConfirmedCases && lastUpdate)
      {  			
        var icon = $("").addClass("material-icons").text("keyboard_arrow_up");
  			$("#confirmedCases").text(whoNumberFormat(totalConfirmedCases));
  			$(".metadata").text(updated_viz_name[cultureIndex] + new Date(lastUpdate).toLocaleDateString(date_localisation_format[cultureIndex], options));             
      }
    else
       {
          window.setTimeout("buildTotalCasesBox(totalConfirmedCases);",100);
       }
}
buildTotalCasesBox(totalConfirmedCases);



