if($('div.sw-blog-posted-by').length) {
changeText = function(text, newText){
    var currentText = $('div.sw-blog-posted-by').html();  
    $('div.sw-blog-posted-by').html(currentText.replace(text,newText));
};
window.setTimeout(function(){changeText(' at ', ' on ')}, 100);
}

