	$(window).load(function() {
		if($(window).width() >= 320 && $(window).width() <= 639){
			calendarListView();
		}
		if($(window).width() >= 1024) {
			extraChannels();

            //added if statement below by Lyndsay - was previous only being called in window.scroll and sometimes not loading - 1.1.16 - Case 00414565
            if($(".sp").length || $(".spn").length) {
				stickyChannelsSub();
				slideIcons();
			}
		}
		searchText();
		setActiveSection();

		if(isMSIE9) {
			$("#hp-slideshow iframe").each(function(){
			      var ifr_source = $(this).attr('src');
			      var wmode = "wmode=transparent";
			      if(ifr_source.indexOf('?') != -1) $(this).attr('src',ifr_source+'&'+wmode);
			      else $(this).attr('src',ifr_source+'?'+wmode);
			});
		}

		if($("#gb-icons.slide-in").length) {
			$("div#ui-mypasskey-overlay").css("right","50px !important");
		}





	});

	$(window).scroll(function(e){
		if($(window).width() >= 1024) { 
			if($(".sp").length || $(".spn").length) {
				stickyChannelsSub();
				slideIcons();
			}
		}
	});

