                "recordAccent": "/cms/lib/CT01903153/Centricity/Template/GlobalAssets/images///Faces/default-man.jpg",
                "recordMoreSwitch": "false",
                "recordMoreText": "",
                "recordMoreLink": "",
                "recordMoreTarget": "_self"
            },{
                "recordSwitch": "false",
                "recordTitle": "",
                "recordCaption": "",
                "recordAccentSwitch": "false",
                "recordAccent": "/cms/lib/CT01903153/Centricity/Template/GlobalAssets/images///Faces/default-man.jpg",
                "recordMoreSwitch": "false",
                "recordMoreText": "Learn More",
                "recordMoreLink": "#",
                "recordMoreTarget": "_self"
            },{
                "recordSwitch": "false",
                "recordTitle": "Photo Title",
                "recordCaption": "Photo Caption",
                "recordAccentSwitch": "false",
                "recordAccent": "/cms/lib/CT01903153/Centricity/Template/GlobalAssets/images///Faces/default-man.jpg",
                "recordMoreSwitch": "false",
                "recordMoreText": "Learn More",
                "recordMoreLink": "#",
                "recordMoreTarget": "_self"
            },{
                "recordSwitch": "false",
                "recordTitle": "Photo Title",
                "recordCaption": "Photo Caption",
                "recordAccentSwitch": "false",
                "recordAccent": "/cms/lib/CT01903153/Centricity/Template/GlobalAssets/images///Faces/default-man.jpg",
                "recordMoreSwitch": "false",
                "recordMoreText": "Learn More",
                "recordMoreLink": "#",
                "recordMoreTarget": "_self"
            },{
                "recordSwitch": "false",
                "recordTitle": "Photo Title",
                "recordCaption": "Photo Caption",
                "recordAccentSwitch": "false",
                "recordAccent": "/cms/lib/CT01903153/Centricity/Template/GlobalAssets/images///Faces/default-man.jpg",
                "recordMoreSwitch": "false",
                "recordMoreText": "Learn More",
                "recordMoreLink": "#",
                "recordMoreTarget": "_self"
            },{
                "recordSwitch": "false",
                "recordTitle": "Photo Title",
                "recordCaption": "Photo Caption",
                "recordAccentSwitch": "false",
                "recordAccent": "/cms/lib/CT01903153/Centricity/Template/GlobalAssets/images///Faces/default-man.jpg",
                "recordMoreSwitch": "false",
                "recordMoreText": "Learn More",
                "recordMoreLink": "#",
                "recordMoreTarget": "_self"
            }];

            var html = "";
            var bulletsHMTL = "";
            var count = 0;

            for(i=0;i<rotatingText.length;i++) {
                if(rotatingText[i].recordSwitch == "true") {
                	count++;
                	if(i == 0) {
                        html += '
';
                        html += 	'
';
                            if(rotatingText[i].recordAccentSwitch == "true" && $.trim(rotatingText[i].recordAccent) != "") {
                                html += '';
                            } else {
                            	html += '';
                            }
                            if($.trim(rotatingText[i].recordTitle) != "") {
                                html += '
'+$.trim(rotatingText[i].recordTitle)+'';
                            }
                            if($.trim(rotatingText[i].recordCaption) != "") {
                                html += '
'+$.trim(rotatingText[i].recordCaption)+'';
                            }
                            if(rotatingText[i].recordMoreSwitch == "true") {
                                html += '
';
                                html += '	'+$.trim(rotatingText[i].recordMoreText)+'';
                                html += '';
                            }
                        html += '	';
                        html += '';
                    }
                    bulletsHMTL += '
';
                }

                $("#play-pause-outer").addClass("rotating-text");
            }

            if(html != "") {
            	$("#hp-slideshow").append('

'+html+'');

                if(false == "true" && !$("#hp-slideshow .hp-rotating-text-item-info .rs-gallery-control").length) {
                    $("#hp-slideshow .hp-rotating-text-outer").addClass("controls")
                    $("#hp-slideshow .hp-rotating-text-item-info").append('
');
                }

                $("#hp-slideshow").append('
'+bulletsHMTL+'');

                $(".hp-rotating-text-bullet:eq(0)").addClass("active");

                var rotatingTextSpeed = 3*1000;
                var rotatingTextAnimateSpeed = rotatingTextSpeed/2;
                var rotatingTextDelay = 8*1000;

                function rotateText(index) {
                    clearInterval(rotatingTextTimer);

                    if(typeof index != "undefined") {
                        $(".hp-rotating-text-bullet").removeClass("active");
                        $(".hp-rotating-text-bullet").eq(index).addClass("active");
                        var i = $(".hp-rotating-text-bullet.active").index();
                    } else {
                        var i = $(".hp-rotating-text-bullet.active").index()+1;
                        if(i == count) {
                            i = 0;
                            $(".hp-rotating-text-bullet").removeClass("active");
                            $(".hp-rotating-text-bullet").eq(i).addClass("active");
                        } else {
                            $(".hp-rotating-text-bullet").removeClass("active");
                            $(".hp-rotating-text-bullet").eq(i).addClass("active");
                        }
                    }

                    if(rotatingText[i].recordAccentSwitch == "true" && $.trim(rotatingText[i].recordAccent) != "") {
                        $(".hp-rotating-text-item-info img").fadeOut(rotatingTextAnimateSpeed, function() {
                            $(".hp-rotating-text-item-info img").attr("src", $.trim(rotatingText[i].recordAccent));
                            $(".hp-rotating-text-item-info img").fadeIn(rotatingTextAnimateSpeed);
                        });
                    } else {
                        $(".hp-rotating-text-item-info img").fadeOut(rotatingTextAnimateSpeed);
                    }

                    if($.trim(rotatingText[i].recordTitle) != "") {
                        $(".hp-rotating-text-item-info h2").fadeOut(rotatingTextAnimateSpeed, function() {
                            $(".hp-rotating-text-item-info h2").text($.trim(rotatingText[i].recordTitle));
                            $(".hp-rotating-text-item-info h2").fadeIn(rotatingTextAnimateSpeed);
                        });
                    } else {
                        $(".hp-rotating-text-item-info h2").fadeOut(rotatingTextAnimateSpeed);
                    }

                    if($.trim(rotatingText[i].recordCaption) != "") {
                        $(".hp-rotating-text-item-info p").fadeOut(rotatingTextAnimateSpeed, function() {
                            $(".hp-rotating-text-item-info p").text($.trim(rotatingText[i].recordCaption));
                            $(".hp-rotating-text-item-info p").fadeIn(rotatingTextAnimateSpeed);
                        });
                    } else {
                        $(".hp-rotating-text-item-info p").fadeOut(rotatingTextAnimateSpeed);
                    }

                    if(rotatingText[i].recordMoreSwitch == "true") {
                        $(".hp-rotating-text-item-more").fadeOut(rotatingTextAnimateSpeed, function() {
                            $(".hp-rotating-text-item-more a").attr({"href": rotatingText[i].recordMoreLink, "target":rotatingText[i].recordMoreTarget});
                            $(".hp-rotating-text-item-more span").text(rotatingText[i].recordMoreText);
                            $(".hp-rotating-text-item-more").fadeIn(rotatingTextAnimateSpeed);
                        });
                    } else {
                        $(".hp-rotating-text-item-more").fadeOut(rotatingTextAnimateSpeed);
                    }

                    setTimeout(function(){ rotatingTextTimer = setInterval(rotateText, rotatingTextDelay) }, rotatingTextSpeed);
                }

                if(count > 1) {
                	var rotatingTextTimer = setInterval(rotateText, rotatingTextDelay);               
                }

                $(".hp-rotating-text-bullet").on("click keydown", function(event){
                    if(event.type == "click" || event.type == "keydown" && (event.keyCode == 32 || event.keyCode == 13)) {
                        var index = $(this).index();
                        rotateText(index);
                    }
                });

                $(".hp-rotating-text-item-info .rs-gallery-control").on("click keydown", function(event){
                    if(event.type == "click" || event.type == "keydown" && (event.keyCode == 32 || event.keyCode == 13)) {
                        if($(this).hasClass("playing")) {
                            $(this).removeClass("playing").addClass("paused").attr("aria-label", "Play Rotating Text");
                            clearInterval(rotatingTextTimer);
                        } else {
                            $(this).removeClass("paused").addClass("playing").attr("aria-label", "Pause Rotating Text");
                            rotatingTextTimer = setInterval(rotateText, rotatingTextDelay);
                        }
                    }
                });
            }
        }
    }

	function slideIcons() {
		var scrollPos = $(window).scrollTop();
        if($("#extra-channels-outer").length) {
				$("#gb-icons").addClass("signed-in");
        }
		if(scrollPos > 0) {
			$("#gb-icons").addClass("slide-in");
            if($("#gb-icons").hasClass("signed-in")) {	//moved this if/else statement to to scrollPos > 0 from scrollPos130 by Lyndsay - 11.3.16 - Case 00418266
					$("#gb-icons.signed-in").css("top","204px"); //changed from 170 by Lyndsay - 11.3.16 - Case 00418266
				} else {
					$("#gb-icons").css("top","192px"); //changed from 320 by Lyndsay - 11.3.16 - Case 00418266
				}
			if(scrollPos > 130 ) {

			}
		} else {
			$("#gb-icons").removeClass("slide-in");
			if($("#gb-icons").hasClass("signed-in")) {
				$("#gb-icons.signed-in").css("top","299px"); //changed from 270 by Lyndsay - 11.3.16 - Case 00418266
			} else {
				$("#gb-icons").css("top","420px");
			}
		}
	}

	function setActiveSection() {
		if($(".sp").length) {
			//ADD CLASS ACTIVE TO ACTIVE SECTION LINK
			var activeSection = "eLearning & COVID-19";
			$("#section-navigation li > a span").each(function() {
				var getText = $(this).text();

				if(getText == activeSection) {
					$(this).parent().parent().addClass("active");
				}
			});

			//SETUP PAGE NAV LINKS UNDER SECTION LINK
			$("
").appendTo("#section-navigation li");
			$(".page-navigation > li").each(function() {
				$(this).appendTo("#section-navigation li.active .cs-section-pages");
			});

			var numSections = $("#section-navigation li.active .cs-section-pages li").size();
			if(numSections < 2) {
				$("#section-navigation li.active .cs-section-pages").hide();
				$("div.ui-widget.app.navigation.sectionnavigation li.active").addClass("no-pages");
			}
		}
	}	

	function pgScreenSize() {
		var getWidth = $(window).width();
		if (getWidth >= 1024) { // 960+
			mediaRotator();
			//movePasskey(); //commented out by Lyndsay - 11.3.16 - Case 00418266
		} if (getWidth >= 768 && getWidth < 1024) { // 768+
			mediaRotator();
			//movePasskey768(); //commented out by Lyndsay - 11.3.16 - Case 00418266
		} if (getWidth >= 640 && getWidth < 768) { // 640+
			mediaRotator();
			//movePasskey768(); //commented out by Lyndsay - 11.3.16 - Case 00418266
		} if (getWidth >= 480 && getWidth < 640) { // 480+
			mediaRotator();
			//movePasskey768(); //commented out by Lyndsay - 11.3.16 - Case 00418266
		} if (getWidth >= 320 && getWidth < 480) { // 320+
			mediaRotator();
			//movePasskey768();
		}
	}

