	function stickyChannelsSub() {
		var scrollPos = $(window).scrollTop();
		var channelHeight = $(".sp #gb-channel-list-outer").height();
		var extraHeight = $(".sp #extra-channels-outer").height() + channelHeight;

		if(!$("#extra-channels-outer").length) {
			if(scrollPos >= 130) {
				$(".sp #gb-channel-list-outer").addClass("fixed");
				$("#sp-content-outer, #spn-content-outer").css("margin-top",channelHeight);
			} else {
				$(".sp #gb-channel-list-outer").removeClass("fixed");
				$("#sp-content-outer, #spn-content-outer").css("margin-top","0");
			}
		} else {
			if(scrollPos >= 130) {
				$(".sp #gb-channel-list-outer, .sp #extra-channels-outer").addClass("fixed");
				$("#sp-content-outer, #spn-content-outer").css("margin-top",extraHeight);
			} else {
				$(".sp #gb-channel-list-outer, .sp #extra-channels-outer").removeClass("fixed");
				$("#sp-content-outer, #spn-content-outer").css("margin-top","0");
			}
		}
	}

