	}

	function mediaRotator() {
		var videoToggle = false;
        var controlsToggle = 'false';
        var bulletsToggle = 'false';

        if(controlsToggle) {
        	controlsToggle = "yes";
            $("#hp-slideshow .rs-gallery-container").addClass("controls");
        } else {
        	controlsToggle ="no";
        }

        if(bulletsToggle) {
        	bulletsToggle = "yes";
        } else {
        	bulletsToggle ="no";
        }

		if(videoToggle == false) {
			$("#hp-slideshow").addClass("photos");
			if(!$("#sw-content-container10.region.ui-hp .rs-gallery-images").length){
				$("#sw-content-container10.ui-hp div.ui-widget.app.cs-rs-multimedia-rotator").csRsMultimediaRotator({
					"imageWidth" 			: 1400,
			        	"imageHeight" 			: 705,
			        	"appHeader" 			: "no",
			        	"titles" 			    : "yes",
			        	"captions" 			    : "yes",
			        	"galleryOverlay" 		: "no",
			       		"linkedElement" 		: ["none"], // ["image", "title", "overlay", "all"]
			        	"playPause" 			: controlsToggle,
			        	"mainControls" 			: "no",
			        	"contMoreLink" 			: "no",
			        	"contMoreLinkText" 		: "Read More",
			        	"descMoreLink" 			: "yes",
			        	"descMoreLinkText" 		: 'Register',
			        	"bullets" 			    : bulletsToggle,
			        	"bulletControls" 		: "no",
			        	"thumbnails" 			: "no",
			        	"thumbnailWidth" 		: 122,
			        	"thumbnailHeight" 		: 53,
			        	"thumbnailSlideDirection" 	: "horizontal", // horizontal or vertical
			        	"hoverPause" 			: "yes",
			        	"fullScreenRotator" 	: "no", // image dimensions work well at 1500x915
			        	"fullScreenVideo" 		: "no",
			        	"transitionType" 		: "fade", // fade or slide
			        	"transitionSpeed" 		: 3,
			        	"transitionDelay" 		: 8,
						"autoRotate" 			: "yes",
			        	"onThumbnailLoad" 		: function() { },
			        	"allThumbnailsLoaded" 	: function() { },
			        	"onTransitionStart" 	: function() { },
			        	"onTransitionEnd" 		: function() { },
			        	"watchVidClick" 		: function() { },
			        	"closeVidClick" 		: function() { },
			        	"allLoaded" 			: function() { 
                        	$("#hp-slideshow .rs-gallery-control.play-pause").appendTo($("#hp-slideshow .rs-gallery-desc"));
                        }
				});
			}
			$("#hp-slideshow").height("auto");
		} else {
			$("#hp-slideshow #sw-content-container10").hide(); 
			if(!$(".msie9").length) {
				//$("#hp-slideshow #player").show();
                if(false) {
					$("#play-pause-outer").show();
                }
			} else {
				$("#hp-slideshow-outer").css({
					"background":"transparent url('/cms/lib/CT01903153/Centricity/Template/16/woodstock-ie9-video-fallback.jpg') no-repeat scroll center top",
					"background-size":"cover"
				});
			}
			var windowHeight = $(window).height();
			$("#hp-slideshow").height(windowHeight);
            if(!$(".hp-rotating-text-item-info").length) {
            	slideshowRotatingText();
            }
		}
	}

