		if(!$("a.ui-btn-toggle.list").hasClass("active")){
			$("a.ui-btn-toggle.list").click();
		}
	}

	function addFramesetTranslate() {
		var pageURL = window.location.href;
		pageURL = encodeURIComponent(pageURL);
		var translateDropdown = 	'English to Spanish' +
						'English to Arabic' +
						'English to Chinese (Simplified)' +
						'English to Chinese (Traditional)' +
						'English to French' +
						'English to German' +
						'English to Italian' +
						'English to Japanese' +
						'English to Korean' +
						'English to Portuguese' +
						'English to Russian';
		var pageHost = window.location.host;
		if(pageHost != "translate.googleusercontent.com")
		{				
			$("#cs-translate-info-inner").html(translateDropdown);
			$(".sw-mystart-dropdown.translate").click(function()
			{
				$(".sw-dropdown",this).show();
			});
			$(".sw-mystart-dropdown.translate").hover(function()
			{
			},
			function()
			{
				$(".sw-dropdown",this).hide();
			});
		}

	}

    function hpBgVideo() {
	  if($("#player").length) {
	        // VIMEO API - https://developer.vimeo.com/player/js-api | https://player.vimeo.com/playground
			// CUSTOMIZE EMBED - https://vimeo.com/blog/post/customize-the-embedded-vimeo-player

			var iframe = $("#vimeoVideo")[0];
			var player = $f(iframe);
			var videoStarted = false;

			// VIMEO PLAYER IS READY
			player.addEvent("ready", function() {
            	$("#player").show();

				// MUTE THE VIDEO
				player.api("setVolume", 0);

				// LOOP THE VIDEO
				if(!$(".ie9").length) {
					player.api("setLoop", true);
				}

				// SHOW THE PLAY/PAUSE BUTTON WHEN VIDEO STARTS
				if(!videoStarted) {
					videoStarted = true;
				}

				// BIND VIDEO EVENTS
				$("#play-pause-btn").click(function() {
                    if($("#play-pause-btn").hasClass("pause")) {
                        $("#play-pause-btn span").text("PLAY VIDEO");
                        $("#play-pause-btn").attr("aria-label","Play");
                        player.api("pause");
                    } else {
                        $("#play-pause-btn span").text("PAUSE VIDEO");
                        $("#play-pause-btn").attr("aria-label","Pause");
                        player.api("play");
                    }
                    $(this).toggleClass("pause");
				});
			});
		}
	}

    function csVideoResize() {
		if($("#player").length) {
			var screenWidth = $(window).width();
			var screenHeight = $(window).height();
			var vidWidth = screenWidth, vidHeight = 0;

			// GET IMAGE RATIO
			var initW = 853.5, initH = 480;
			var ratio = initH / initW;
			vidHeight = vidWidth * ratio;

			// APPLY THE DIMENSIONS
			$("#player, #hp-slideshow, #vimeoVideo").css({"height": vidHeight + "px"});
		}
	}

