link.script:
function toggleMenu() {
	if (jQuery('#mobile-menu').is(":visible")) {
		jQuery('#mobile-menu').hide('slow');
		jQuery('#showMenuLabel').html('Show Menu');
	} else {
		jQuery('#mobile-menu').show('slow');
		jQuery('#showMenuLabel').html('Hide Menu');
	}
}

jQuery(document).ready(function(){
	jQuery('.newsTicker').newsTicker({
		row_height: 90,
		max_rows: 3,
		duration: 4000,
	    nextButton:  jQuery('#news-up'),
	    prevButton:  jQuery('#news-down'),
	});
});

