        "WindowLoad": function() {
			this.JsMediaQueries();	
            csGlobalJs.OpenInNewWindowWarning();
        },

        "WindowResize": function() {
            this.JsMediaQueries();
            this.StickyChannelBar();
        },

        "WindowScroll": function() {
            this.StickyChannelBar();
        },

        "JsMediaQueries": function() {
        	var mmgArrays = this.GatherMMGs();

            switch(this.GetBreakPoint()) {
                case "desktop": 
					this.DivideEvents();
                    this.DivideShortcuts();

                    if($(".cs-article-slider .cs-global-icon").length > 5) {
                    	$(".cs-global-icons-app .cs-global-icons").removeClass("flex-justify-center");
                        $(".cs-global-icons-app .cs-article-slider-control.back").css("display", "inline-block").prependTo($(".cs-global-icons-app .ui-widget-footer"));
                        $(".cs-global-icons-app .cs-article-slider-control.next").css("display", "inline-block").appendTo($(".cs-global-icons-app .ui-widget-footer"));
                    } else {
                    	$(".cs-global-icons-app .cs-global-icons").addClass("flex-justify-center");
                    	$(".cs-global-icons-app .cs-article-slider-control").hide();
                    }                    
                break;
                case "768":
					this.DivideEvents();
                    this.DivideShortcuts();

                    if($(".cs-article-slider .cs-global-icon").length > 3) {
                    	$(".cs-global-icons-app .cs-global-icons").removeClass("flex-justify-center");
                        $(".cs-global-icons-app .cs-article-slider-control.back").css("display", "inline-block").prependTo($(".cs-global-icons-app .ui-widget-footer"));
                        $(".cs-global-icons-app .cs-article-slider-control.next").css("display", "inline-block").appendTo($(".cs-global-icons-app .ui-widget-footer"));
                    } else {
                    	$(".cs-global-icons-app .cs-global-icons").addClass("flex-justify-center");
                    	$(".cs-global-icons-app .cs-article-slider-control").hide();
                    }

                    //DOC set height on mmg desc to keep content from jumping
                    var scrolled = 0;
                    $(window).scroll(function(e) {
                        scrolled = $(window).scrollTop();   
                       	$.each(mmgArrays, function(index, mmg) {
                            if(scrolled >= mmg.loc && mmg.scrollLock === 0) {
                                $(".mmg-description-outer", mmg.appID).height($(".mmg-description-outer", mmg.appID).height());
                                mmg.scrollLock = 1;
                            } else if(scrolled < mmg.loc && mmg.scrollLock == 1){
                                $(".mmg-description-outer", mmg.appID).height("auto");
                                mmg.scrollLock = 0;
                            }
                        });                     
                    }); 
                break;
   				case "640":
					this.DivideEvents();
                    this.DivideShortcuts();

                    $(".cs-mystart-dropdown.translate .cs-selector").text("Translate Language");

                    if($(".cs-article-slider .cs-global-icon").length > 2)  {
                    	$(".cs-global-icons-app .cs-global-icons").removeClass("flex-justify-center");
                        $(".cs-global-icons-app .cs-article-slider-control.back").css("display", "inline-block").prependTo($(".cs-global-icons-app .ui-widget-footer"));
                        $(".cs-global-icons-app .cs-article-slider-control.next").css("display", "inline-block").appendTo($(".cs-global-icons-app .ui-widget-footer"));
                    } else {
                    	$(".cs-global-icons-app .cs-global-icons").addClass("flex-justify-center");
                    	$(".cs-global-icons-app .cs-article-slider-control").hide();
                    }

                    //DOC set height on mmg desc to keep content from jumping
                    var scrolled = 0;
                    $(window).scroll(function(e) {
                        scrolled = $(window).scrollTop();   
                       	$.each(mmgArrays, function(index, mmg) {
                            if(scrolled >= mmg.loc && mmg.scrollLock === 0) {
                                $(".mmg-description-outer", mmg.appID).height($(".mmg-description-outer", mmg.appID).height());
                                mmg.scrollLock = 1;
                            } else if(scrolled < mmg.loc && mmg.scrollLock == 1){
                                $(".mmg-description-outer", mmg.appID).height("auto");
                                mmg.scrollLock = 0;
                            }
                        });                     
                    }); 
                break;
                case "480":
                	this.DivideEvents();
                    this.DivideShortcuts();


                	$(".cs-mystart-dropdown.translate .cs-selector").text("Translate Language");    

                    if($(".cs-article-slider .cs-global-icon").length > 2)  {
                    	$(".cs-global-icons-app .cs-global-icons").removeClass("flex-justify-center");
                        $(".cs-global-icons-app .cs-article-slider-control.back").css("display", "inline-block").prependTo($(".cs-global-icons-app .ui-widget-footer"));
                        $(".cs-global-icons-app .cs-article-slider-control.next").css("display", "inline-block").appendTo($(".cs-global-icons-app .ui-widget-footer"));
                    } else {
                    	$(".cs-global-icons-app .cs-global-icons").addClass("flex-justify-center");
                    	$(".cs-global-icons-app .cs-article-slider-control").hide();
                    }

                    //DOC set height on mmg desc to keep content from jumping
                    var scrolled = 0;
                    $(window).scroll(function(e) {
                        scrolled = $(window).scrollTop();   
                       	$.each(mmgArrays, function(index, mmg) {
                            if(scrolled >= mmg.loc && mmg.scrollLock === 0) {
                                $(".mmg-description-outer", mmg.appID).height($(".mmg-description-outer", mmg.appID).height());
                                mmg.scrollLock = 1;
                            } else if(scrolled < mmg.loc && mmg.scrollLock == 1){
                                $(".mmg-description-outer", mmg.appID).height("auto");
                                mmg.scrollLock = 0;
                            }
                        });                     
                    });                  
                break;
                case "320":
					this.DivideEvents();
                    this.DivideShortcuts();

                	$(".cs-mystart-dropdown.translate .cs-selector").text("Translate");   

                    if($(".cs-article-slider .cs-global-icon").length > 1)  {
                    	$(".cs-global-icons-app .cs-global-icons").removeClass("flex-justify-center");
                        $(".cs-global-icons-app .cs-article-slider-control.back").css("display", "inline-block").prependTo($(".cs-global-icons-app .ui-widget-footer"));
                        $(".cs-global-icons-app .cs-article-slider-control.next").css("display", "inline-block").appendTo($(".cs-global-icons-app .ui-widget-footer"));
                    } else {
                    	$(".cs-global-icons-app .cs-global-icons").addClass("flex-justify-center");
                    	$(".cs-global-icons-app .cs-article-slider-control").hide();
                    }

                    //DOC set height on mmg desc to keep content from jumping
                    var scrolled = 0;
                    $(window).scroll(function(e) {
                        scrolled = $(window).scrollTop();   
                       	$.each(mmgArrays, function(index, mmg) {
                            if(scrolled >= mmg.loc && mmg.scrollLock === 0) {
                                $(".mmg-description-outer", mmg.appID).height($(".mmg-description-outer", mmg.appID).height());
                                mmg.scrollLock = 1;
                            } else if(scrolled < mmg.loc && mmg.scrollLock == 1){
                                $(".mmg-description-outer", mmg.appID).height("auto");
                                mmg.scrollLock = 0;
                            }
                        });                     
                    });                 
