		"DropdownActions": function(params) {
        	// FOR SCOPE
            var template = this;

            var dropdownParent = params.dropdownParent;
            var dropdownSelector = params.dropdownSelector;
            var dropdown = params.dropdown;
            var dropdownList = params.dropdownList;

            $(dropdownParent + " " + dropdownList + " a").attr("tabindex", "-1");

            // MYSTART DROPDOWN SELECTOR CLICK EVENT
            $(dropdownParent).on("click", dropdownSelector, function(e) {
                e.preventDefault();

                if($(this).parent().hasClass("open")){
                	$("+ " + dropdownList + " a").attr("tabindex", "-1");
                    $(this).attr("aria-expanded", "false").parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing"); 
                } else {
                	$(this).attr("aria-expanded", "true").parent().addClass("open").find(dropdown).attr("aria-hidden","false").slideDown(300, "swing");
                }
        	});

            // MYSTART DROPDOWN SELECTOR KEYDOWN EVENTS
            $(dropdownParent).on("keydown", dropdownSelector, function(e) {
                // CAPTURE KEY CODE
                switch(e.keyCode) {
                    // CONSUME LEFT AND UP ARROWS
                    case template.KeyCodes.enter:
                    case template.KeyCodes.space:
                        e.preventDefault();

                        // IF THE DROPDOWN IS OPEN, CLOSE IT
                        if($(dropdownParent).hasClass("open")){
                        	$("+ " + dropdown + " " + dropdownList + " a").attr("tabindex", "-1");
                            $(this).attr("aria-expanded", "false").parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing"); 
                        } else {
                        	$(this).attr("aria-expanded", "true").parent().addClass("open").find(dropdown).attr("aria-hidden", "false").slideDown(300, "swing", function(){
                                $(dropdownList + " li:first-child a", this).attr("tabindex", "0").focus();
                            });
                        }
                    break;

                    // CONSUME TAB KEY
                    case template.KeyCodes.tab:
                        if($("+ " + dropdown + " " + dropdownList + " a").length) {
                        	$("+ " + dropdown + " " + dropdownList + " a").attr("tabindex", "-1");
                        	$(this).attr("aria-expanded", "false").parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing");
                        }
                    break;

                    // CONSUME LEFT AND UP ARROWS
                    case template.KeyCodes.down:
                    case template.KeyCodes.right:
                        e.preventDefault();

                        $("+ " + dropdown + " " + dropdownList + " a").attr("tabindex", "-1");
                        $("+ " + dropdown + " " + dropdownList + " li:first-child > a", this).attr("tabindex", "0").focus();
                    break;
                }
            });

            // MYSTART DROPDOWN LINK KEYDOWN EVENTS
            $(dropdownParent).on("keydown", dropdownList + " li a", function(e) {
                // CAPTURE KEY CODE
                switch(e.keyCode) {
                    // CONSUME LEFT AND UP ARROWS
                    case template.KeyCodes.left:
                    case template.KeyCodes.up:
                        e.preventDefault();

                        // IS FIRST ITEM
                        if($(this).parent().is(":first-child")) {
                            // FOCUS DROPDOWN BUTTON
                            $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                            $(this).closest(dropdownParent).find(dropdownSelector).focus();
                        } else {
                            // FOCUS PREVIOUS ITEM
                            $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                            $(this).parent().prev("li").find("> a").attr("tabindex", "0").focus();
                        }
                    break;

                    // CONSUME RIGHT AND DOWN ARROWS
                    case template.KeyCodes.right:
                    case template.KeyCodes.down:
                        e.preventDefault();

                        // IS LAST ITEM
                        if($(this).parent().is(":last-child")) {
                            // FOCUS FIRST ITEM
                            $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                            $(this).closest(dropdownList).find("li:first-child > a").attr("tabindex", "0").focus();
                        } else {
                            // FOCUS NEXT ITEM
                            $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                            $(this).parent().next("li").find("> a").attr("tabindex", "0").focus();
                        }
                    break;

                    // CONSUME TAB KEY
                    case template.KeyCodes.tab:
                    	if(e.shiftKey) {
                        	e.preventDefault();

                        	// FOCUS DROPDOWN BUTTON
                            $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                            $(this).closest(dropdownParent).find(dropdownSelector).focus();
                        }
                    break;

                    // CONSUME HOME KEY
                    case template.KeyCodes.home:
                        e.preventDefault();

                        // FOCUS FIRST ITEM
                        $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                        $(this).closest(dropdownList).find("li:first-child > a").attr("tabindex", "0").focus();
                    break;

                    // CONSUME END KEY
                    case template.KeyCodes.end:
                        e.preventDefault();

                        // FOCUS LAST ITEM
                        $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                        $(this).closest(dropdownList).find("li:last-child > a").attr("tabindex", "0").focus();
                    break;
                }
            });

            $(dropdownParent).mouseleave(function() {
				$(dropdownList + " a", this).attr("tabindex", "-1");
				$(dropdownSelector, this).attr("aria-expanded", "false").parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing"); 
            }).focusout(function() {
                var thisDropdown = this;

                setTimeout(function () {
                    if(!$(thisDropdown).find(":focus").length) {
                        $(dropdownSelector, thisDropdown).attr("aria-expanded", "false").parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing");
                    }
                }, 500);
            });
        },

		"Header": function() {
            // ADD LOGO
            var logoSrc = jQuery.trim("/cms/lib/GA50000511/Centricity/Template/GlobalAssets/images///logos/GrowClinchSchools1.png"); 
            var srcSplit = logoSrc.split("/");
            var srcSplitLen = srcSplit.length;

			if((logoSrc != "") && (srcSplit[srcSplitLen - 1] != "default-man.jpg")) {
				$("#gb-logo").append("").removeClass("hidden");
			} else {
				$("#gb-logo").append("").removeClass("hidden");
			}

            if($(".sitename-one").text() == "") {
            	$(".sitename-one").remove();
            }

            // HIDE HEADER LINKS FOR ACCESSIBILITY IF TURNED OFF
            $('.gb-header.right li').each(function() {
            	if($('a[data-switch="false"]', this).length || $('a', this).attr("href") == "" || $('a', this).text() == "") {
                	$(this).remove();
				}
            });

            if($('.gb-header.right li').length == 0) {
            	$('.gb-header.right ul').remove();
            	$('.gb-header.right').hide();
            	$('.gb-header.left').css("border", "none");
            }
        },

        "ChannelBar": function() {
            $(".sw-channel-item").unbind('hover');

            $(".sw-channel-item").hover(function(){
                $(".sw-channel-item ul").stop(true, true);
                var subList = $(this).children('ul');
                if ($.trim(subList.html()) !== "") {
                    subList.slideDown(300, "swing");
                }
                $(this).addClass("hover");
            }, function(){
                $(".sw-channel-dropdown").slideUp(300, "swing");
                $(this).removeClass("hover");
            });

            $(".sw-channel-item").each(function(){
            	if($(".sw-channel-dropdown li", this).length == 0) {
                	$(this).addClass("no-dropdown");
                }
            });
        },

        "StickyChannelBar": function() {
			if(!this.IsMyViewPage) {
                switch(this.GetBreakPoint()) {
                    case "desktop":
                        var navOffSet = $("nav").offset().top;
                        if ($(window).scrollTop() <= navOffSet) {
                            if(this.ChannelBarIsSticky) {
                                $("nav").removeClass("sticky").removeAttr("style");

                                this.ChannelBarIsSticky = false;
                            }

                            if($(".sp").length && $(".ui-widget.app.calendar").length) {
                                $(".wcm-controls").css("margin-top", "0px");
                            }
                        } else {
                            $("nav").css("height", $("#gb-channel-list-outer").outerHeight(true));

                            if(!this.ChannelBarIsSticky) {
                                $("nav").addClass("sticky");

                                this.ChannelBarIsSticky = true;
                            }

                            if($(".sp").length && $(".ui-widget.app.calendar").length) {
                            	if($(".wcm-controls").hasClass("wcm-stuck")) {
                                	$(".wcm-controls").css("margin-top", $("#gb-channel-list-outer").outerHeight(true));
                                }
                            }
                        }

                        $("#sw-maincontent").css({
                            "display": "block",
                            "position": "relative",
                            "top": "-" + $("nav").outerHeight(true) + "px"
                        });
                    break;
                    case "768": case "640": case "480": case "320":
                        if(this.ChannelBarIsSticky) {
                            $("nav").removeClass("sticky").removeAttr("style");

                            this.ChannelBarIsSticky = false;
                        }

                        if($(".sp").length && $(".ui-widget.app.calendar").length) {
                            $(".wcm-controls").css("margin-top", "0px");
                        }
                        $("#sw-maincontent").removeAttr("style");
                    break;
                }
            }
        },

        "Body": function() {
        	// FOR SCOPE
            var _this = this;

            //Check default image for region a
            var regionASrc = jQuery.trim("/cms/lib/GA50000511/Centricity/Template/GlobalAssets/images///Faces/default-man.jpg"); 
            var regionASrcSplit = regionASrc.split("/");
            var regionASrcSplitLen = regionASrcSplit.length;

			if((regionASrc != "") && (regionASrcSplit[regionASrcSplitLen - 1] != "default-man.jpg")) {

			} else {
               $('#hp-slideshow-background .hp-slideshow-background-image').css({
                   "background": "url('/cms/lib/GA50000511/Centricity/Template/18/defaults/backgrounds-region-a.jpg') no-repeat center center/cover fixed"
               });
            }

            //Check default image for region b and e
            var regionBSrc = jQuery.trim("/cms/lib/GA50000511/Centricity/Template/GlobalAssets/images///Faces/default-man.jpg"); 
            var regionBSrcSplit = regionBSrc.split("/");
            var regionBSrcSplitLen = regionBSrcSplit.length;

			if((regionBSrc != "") && (regionBSrcSplit[regionBSrcSplitLen - 1] != "default-man.jpg")) {

			} else {
               $('[data-region="b"] .hp-content-background-image').css({
                   "background": "url('/cms/lib/GA50000511/Centricity/Template/18/defaults/backgrounds-region-b.png') no-repeat center center/cover fixed"
               });
            }

            var regionESrc = jQuery.trim("/cms/lib/GA50000511/Centricity/Template/GlobalAssets/images///Faces/default-man.jpg"); 
            var regionESrcSplit = regionESrc.split("/");
            var regionESrcSplitLen = regionESrcSplit.length;

            if((regionESrc != "") && (srcSplit[srcSplitLen - 1] != "default-man.jpg")) {

			} else {
               $('[data-region="e"] .hp-content-background-image').css({
                   "background": "url('/cms/lib/GA50000511/Centricity/Template/18/defaults/backgrounds-region-e.png') no-repeat center center/cover fixed"
               });
            }

            // ADD CLASS TO NAV IF DASHBOARD IS ACTIVE
            if($("#dashboard-sidebar").length) {
                $("nav").addClass("dashboard-active");
            }

            // AUTO FOCUS SIGN IN FIELD
            $("#swsignin-txt-username").focus();

            // APPLY RESPONSIVE DIMENSIONS TO CONTENT IMAGES
            $(".ui-widget.app .ui-widget-detail img")
                .not($(".ui-widget.app.multimedia-gallery .ui-widget-detail img"))
                .each(function() {
                    if ($(this).attr('width') !== undefined && $(this).attr('height') !== undefined) { // IMAGE HAS INLINE DIMENSIONS
                        $(this).css({"display": "inline-block", "width": "100%", "max-width": $(this).attr("width") + "px", "height": "auto", "max-height": $(this).attr("height") + "px"});
                    }
            });

            // ADJUST FIRST BREADCRUMB
            $("li.ui-breadcrumb-first > a > span").text("Home");

            // HOMEPAGE APP SLIDER
            $(".ui-hp .headlines, .ui-hp .announcements").csArticleSlider({
                "viewerNum": [3, 3, 2, 1, 1], // NUMERICAL - [960 view, 768 view, 640 view, 480 view, 320 view]
                "transitionSpeed": .5 // SECONDS
            });

            // WRAP APP SLIDER CONTROLS INTO ONE ELEMENT
			$(".ui-hp .app").each(function() {
            	$(".app-level-social-follow:empty", this).remove();
                $(".ui-read-more", this).prependTo($(".ui-widget-footer", this));

                // TRIM ALL FOOTERS FOR EXTRA WHITE SPACE
                var text = $.trim($(".ui-widget-footer", this).text());
                if(text == "") { 
                    $(".ui-widget-footer", this).text(text);
                }

                $(".ui-widget-footer .clear", this).remove();
                $(".ui-widget-footer", this).addClass("flex flex-items-center flex-justify-center");

            	if($(".ui-widget-footer > *", this).length) {
                    $(".ui-widget-footer", this).removeClass("flex-justify-center").addClass("flex-justify-space").wrapInner('
');
                    $(".ui-read-more", this).prependTo($(".ui-widget-footer .cs-footer-items", this));
                }

                if($(".ui-articles li", this).length >= 4) {
                    $(".cs-article-slider-control.back", this).removeAttr("style").prependTo($(".ui-widget-footer", this));
                    $(".cs-article-slider-control.next", this).removeAttr("style").appendTo($(".ui-widget-footer", this));
                }

                if($(".ui-articles li", this).length < 4) {
                 	$(".ui-widget-footer", this).removeClass("flex-justify-space").addClass("flex-justify-center")
                	$(".cs-article-slider-control", this).hide();
                } 

                if($(".ui-articles li", this).length < 3) {
                	$(".ui-articles", this).addClass("flex-justify-center");
                    $(".cs-article-slider-control", this).hide();
                }
            });           

            // MOVE MORE LINK ON SUBPAGES
            $(".ui-sp .app").each(function() {
                $(".ui-widget-footer", this).addClass("flex flex-items-center");

                $(".ui-widget-footer .clear", this).remove();

                $(".ui-read-more", this).prependTo($(".ui-widget-footer", this));
            });

            // WRAP HEADLINES TITLE AND TEXT INTO ONE ELEMENT
			$(".ui-hp .headlines .ui-article").each(function(){
                $(".ui-article-title, .ui-article-description", this).wrapAll('
');
            });

            // SET GLOBAL VARS FOR ALL HOMEPAGE SITE SHORTCUTS APPS
            $(".hp .siteshortcuts").each(function() {
                var appID = $(this).closest("div[id*='pmi-']").attr("id").replace("pmi-", "");
                window["app" + appID] = $(this).html();
           	});

            // USE CHANNEL NAME FOR PAGELIST NAV HEADER IF ONE IS NOT PRESENT
            if(!$("div.sp-column.one .ui-widget-header h1").length) {
                $("div.sp-column.one .ui-widget-header").append("
");
            }
        },

