        "Slideshow": function() {
        	// FOR SCOPE
            var _this = this;

            // FOR #HP-SLIDESHOW
            if($(".ui-hp").length && $("#sw-content-container10.ui-hp .ui-widget.app.multimedia-gallery").length) {
                this.MMGPlugin();
            }

            // MMG PLACEHOLDER
            if($(".ui-hp").length && !$("#sw-content-container10.ui-hp .ui-widget.app.multimedia-gallery").length) {
                $.csMultimediaGalleryDefault({
                    "contentRegion" : "#sw-content-container10.ui-hp",
                    "defaultImageSrc" : "/cms/lib/GA50000511/Centricity/Template/18/defaults/slideshow-default.jpg",
                    "allLoaded": function() {
                        _this.MMGPlugin();
                    }
                });
            }

            // FOR HOMEPAGE REGION B
            if($('[data-region="b"] .ui-widget.app.multimedia-gallery').length) {
                var mmg = eval("multimediaGallery" + $('[data-region="b"] .ui-widget.app.multimedia-gallery:first').attr("data-pmi"));
                mmg.props.defaultGallery = false;

                $('[data-region="b"] .ui-widget.app.multimedia-gallery:first').csMultimediaGallery({
                    "efficientLoad" : false,
                    "imageWidth" : 606,
                    "imageHeight" : 404,
                    "mobileDescriptionContainer": [768, 640, 480, 320], // [960, 768, 640, 480, 320]
                    "playPauseControl" : false,
                    "backNextControls" : true,
                    "autoRotate" : false,
                    "hoverPause" : false,
                    "onTransitionStart" : function(props) {
                    	// props.element, props.currentRecordIndex, props.currentGalleryIndex, props.nextRecordIndex, props.nextGalleryIndex, props.mmgRecords
                        $(".mmg-slide", props.element).each(function(index) {
                            $(".read-more", this).html(''+props.mmgRecords[index].linkText+'');
                            $(".watch-video", this).html(''+props.mmgRecords[index].videoLinkText+'');
                        });

                    	$(".mmg-description-outer", props.element).each(function(index) {
                            $(".read-more", this).html(''+props.mmgRecords[index].linkText+'');
                            $(".watch-video", this).html(''+props.mmgRecords[index].videoLinkText+'');
                        });
                    },
                    "allLoaded" : function(props) { // props.element, props.currentRecordIndex, props.currentGalleryIndex, props.mmgRecords
                    	// MOVE CONTROLS 
                        $(".mmg-control.next, .mmg-control.back", props.element).appendTo($(".mmg-container", props.element).parent()).addClass("cs-html-btn-reset").wrapAll('
');

                    	// SET TABINDEX TO 0
                        $(".mmg-slide", props.element).attr({"tabindex": 0, "aria-hidden": false});

                        // SPLIT IMAGES INTO 2 COLUMNS
                        if(props.mmgRecords.length > 3) {
                        	$(".mmg-slides-outer", props.element).append('');
                            $(".mmg-slide:nth-child(n+4)", props.element).appendTo($(".mmg-slides", props.element).eq(1));
                        }

						// ADD IN ICONS FOR LINKS, ADD BACK TEXT FOR ACCESSIBILITY
                        $(".mmg-slide", props.element).each(function(index) {
                        	if(!$(".mmg-description", this).length) {
                            	$(this).addClass("cs-no-hover");
                            } else {
                        		$(".mmg-description", this).wrapInner('
');
                        	}

                            $(".read-more", this).html(''+props.mmgRecords[index].linkText+'');
                            $(".watch-video", this).html(''+props.mmgRecords[index].videoLinkText+'');
                        });

                        $(".mmg-description-outer", props.element).each(function(index) {
                            $(".read-more", this).html(''+props.mmgRecords[index].linkText+'');
                            $(".watch-video", this).html(''+props.mmgRecords[index].videoLinkText+'');
                        });


                        // IF SLIDEOR ANY CHILD ELMENT IS FOCUSED, KEEP FOCUS
                        $(".mmg-slide", props.element).on('focusin', function() {
                            $(this).addClass("focus");
                        });

                        $(".mmg-slide", props.element).on('focusout', function() {
                            var $elem = $(this);
                            setTimeout(function() {
                                var hasFocus = !! ($elem.find(':focus').length > 0);
                                if (! hasFocus) {
                                    $elem.removeClass("focus");
                                }
                            }, 10);
                        });
                    },
                    "onWindowResize": function(props) {
                  		$(".mmg-slide", props.element).each(function(index) {
                            $(".read-more", this).html(''+props.mmgRecords[index].linkText+'');
                            $(".watch-video", this).html(''+props.mmgRecords[index].videoLinkText+'');
                        });

                    	$(".mmg-description-outer", props.element).each(function(index) {
                            $(".read-more", this).html(''+props.mmgRecords[index].linkText+'');
                            $(".watch-video", this).html(''+props.mmgRecords[index].videoLinkText+'');
                        });                    
                    }
                });
            }

             // FOR HOMEPAGE REGION E
            if($('[data-region="e"] .ui-widget.app.multimedia-gallery').length) {
                var mmg = eval("multimediaGallery" + $('[data-region="e"] .ui-widget.app.multimedia-gallery:first').attr("data-pmi"));
                mmg.props.defaultGallery = false;

                $('[data-region="e"] .ui-widget.app.multimedia-gallery:first').csMultimediaGallery({
                    "efficientLoad" : false,
                    "imageWidth" : 606,
                    "imageHeight" : 404,
                    "mobileDescriptionContainer": [768, 640, 480, 320], // [960, 768, 640, 480, 320]
                    "playPauseControl" : false,
                    "backNextControls" : true,
                    "autoRotate" : false,
                    "hoverPause" : false,
                    "onTransitionStart" : function(props) {
                    	// props.element, props.currentRecordIndex, props.currentGalleryIndex, props.nextRecordIndex, props.nextGalleryIndex, props.mmgRecords
                        // ADD IN ICONS FOR LINKS, ADD BACK TEXT FOR ACCESSIBILITY
                        $(".mmg-slide", props.element).each(function(index) {
                            $(".read-more", this).html(''+props.mmgRecords[index].linkText+'');
                            $(".watch-video", this).html(''+props.mmgRecords[index].videoLinkText+'');
                        });

                    	$(".mmg-description-outer", props.element).each(function(index) {
