                            $(".watch-video", this).html(''+props.mmgRecords[index].videoLinkText+'');
                        });
                    },
                    "allLoaded" : function(props) { // props.element, props.currentRecordIndex, props.currentGalleryIndex, props.mmgRecords
                    	// MOVE CONTROLS 
                        $(".mmg-control.next, .mmg-control.back", props.element).appendTo($(".mmg-container", props.element).parent()).addClass("cs-html-btn-reset").wrapAll('
